//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//definice globaln√≠ch funkci
const fSendUrl = global.get('fSendUrl');

if (fSendUrl) {
    const nRelayNumber = 0;
    const sIpAddress = "192.168.11.7";
    let sTurn = "off";  // V√Ωchoz√≠ hodnota
    let nTurn = 0;  // V√Ωchoz√≠ hodnota
    let n_trigger_MPTT = 500; // defaultn√≠ hodnota triggeru
    let _url = "";

    //Naƒçten√≠ vstupn√≠ch dat
    const _general_stop = msg.payload._general_stop;// general stop

    // Naƒçteni glob√°ln√≠ch dat
    const pvPower = global.get("pvPower") || 0; // Hodnota z registru 819 

    if (_general_stop == true){
        sTurn = "off";
        nTurn = 0;
    }else{// Zkontroluj, zda pvPower je vƒõt≈°√≠ nebo rovno 1200 a nastav sTurn podle toho
        if (pvPower >= n_trigger_MPTT) {
            sTurn = "on";
            nTurn = 1;
        } else {
            sTurn = "off";
            nTurn = 0;
        }
    }

    // Vytvo≈ôit URL na z√°kladƒõ hodnoty sTurn
    _url = fSendUrl(sIpAddress, nRelayNumber, sTurn);

    // Nastavit payload zpr√°vy
    msg.payload = {
        _url_m: _url,
        pvPower: pvPower,
        nTurn: nTurn,
        sTurn: sTurn + " MPTT"
    };

    // Debug v√Ωpisy
    if (debug) {
        node.warn("pvPower: " + pvPower);
        node.warn("sTurn: " + sTurn);
        node.warn("URL: " + _url);
    }
    } else {
    node.error("Glob√°ln√≠ funkce nebyly nalezeny. <- FORM CHLAZENI FVE -- MPTT");
}
return msg;

