// Pou≈æ√≠v√°n√≠ tƒõchto str√°nek a produkt≈Ø ke komerƒçn√≠m √∫ƒçel≈Øm lze jen se souhlasem autora.
// V≈°echna pr√°va vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//definice globaln√≠ch funkci
const fSendUrl = global.get('fSendUrl');
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');

if (fSendUrl && nConvertSignetUnsignet) {

    const nRelayNumber = 0;
    const sIpAddress = "192.168.11.9";
    let sTurn = "off";  // V√Ωchoz√≠ hodnota
    let nTurn = 0;  // V√Ωchoz√≠ hodnota
    let n_trigger_Changer = 700; // defaultn√≠ hodnota triggeru
    let _url = "";

    //Naƒçten√≠ vstupn√≠ch dat
    const _general_stop = msg.payload._general_stop;// general stop

    // Definice fazi AC LOAD
    const phase1 = global.get("phase1") || 0; // Hodnota z registru 817
    const phase2 = global.get("phase2") || 0;  // Hodnota z registru 818
    const phase3 = global.get("phase3") || 0;;  // Hodnota z registru 819

    // Definice Gridu
    const grid1 = global.get("grid1") || 0; // Hodnota z registru 811
    const grid2 = global.get("grid2") || 0;  // Hodnota z registru 812
    const grid3 = global.get("grid3") || 0;;  // Hodnota z registru 813

    // Spoƒç√≠tat celkov√Ω odbƒõr AC z√°tƒõ≈æe
    let totalACLoad = phase1 + phase2 + phase3;

    //Spoƒç√≠tat celkov√Ω odber nebo dod√°vku do GRIDu
    let _ABS_totalGrid = Math.abs(grid1 + grid2 + grid3); //vzdy kladna hodnota

    if (_general_stop == true){
        sTurn = "off";
        nTurn = 0;
    }else{// Zkontroluj, zda totalACLoad nebo  _ABS_totalGrid je vƒõt≈°√≠ jako n_trigger_Changer a podle toho nastav sTurn.
        if (totalACLoad > n_trigger_Changer || _ABS_totalGrid > n_trigger_Changer) {
            sTurn = "on";
            nTurn = 1;
        } else {
            sTurn = "off";
            nTurn = 0;
        }
    }

    // Vytvo≈ôit URL na z√°kladƒõ hodnoty sTurn
    _url = fSendUrl(sIpAddress, nRelayNumber, sTurn);

    // Nastavit payload zpr√°vy
    msg.payload = {
        _url_ch: _url,
        _ABS_totalGrid: _ABS_totalGrid,
        totalACLoad: totalACLoad,
        sTurn: sTurn + " CHANGER",
        nTurn: nTurn
    };

    // Debug v√Ωpisy
    if (debug) {
        node.warn("totalACLoad: " + totalACLoad);
        node.warn("sTurn: " + sTurn);
        node.warn("URL: " + _url);
        node.warn("_ABS_totalGrid: " + _ABS_totalGrid);
    }
    } else {
    node.error("Glob√°ln√≠ funkce nebyly nalezeny. <- FORM CHLAZENI FVE -- CHANGER");
}
return msg;

