        //const grid2 = (nConvertSignetUnsignet(parseFloat(msg.payload.grid2))  || global.get('grid2')) ?? 0; // Hodnota z registru 812
        //const grid3 = (nConvertSignetUnsignet(parseFloat(msg.payload.grid3))  || global.get('grid3')) ?? 0;  // Hodnota z registru 813
