//
// This code is distributed under the GNU Public License
// V≈°echny informace jsou zahrnuty pod GPL licenci, pokud nen√≠ explicitnƒõ uveden jin√Ω typ licence.
// Pou≈æ√≠v√°n√≠ tƒõchto str√°nek a produkt≈Ø ke komerƒçn√≠m √∫ƒçel≈Øm lze jen se souhlasem autora.
// V≈°echna pr√°va vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//definice globaln√≠ch funkci
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; //dotaz na data z globalni strukturu

if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier) {
    if ("tomorrowHourlyPrices" in msg.payload) {
        var line1 = [];
        var line2 = [];
        const currentTime = new Date();
        const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); // prida CZ lokaci
        const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate)); // upravi stavajici objekt cas na prijatelnou podobu pro porovnavani

        var hourlyPrices = msg.payload.tomorrowHourlyPrices;

        var minPrice = Math.min(...hourlyPrices);
        var maxPrice = Math.max(...hourlyPrices);

        for (var i = 0; i < 24;) {

            // spot price
            line1[i] = { "x": new Date(oDate_CZ.setUTCHours(i, 0, 0)), "y": hourlyPrices[i] };
            // spot treshold
            line2[i] = { "x": new Date(oDate_CZ.setUTCHours(i, 0, 0)), "y": spotTresholdPrice };

            if (debug) {
                //   node.warn("oDate_CZ: " + oDate_CZ);
                //   node.warn("i: " + i);
                //   node.warn("hourlyPrices[i] : " + hourlyPrices[i] );
            }
            i++;
        }

        // add one more point to finish chart line
        line1[24] = { "x": new Date(oDate_CZ.setUTCHours(23, 59, 59)), "y": hourlyPrices[23] };
        line2[24] = { "x": new Date(oDate_CZ.setUTCHours(23, 59, 59)), "y": spotTresholdPrice };

        msg.payload = [{
            "series": ["Kƒç/kWh", "Spot limit"],
            "data": [line1, line2]
        }];

        msg.minmax = minPrice.toFixed(2) + " | " + maxPrice.toFixed(2);
    } else {
        // clear chart data to reset, data not available
        msg.payload = [];
    }

} else {// nenalezeny globalni funkce
    node.error("Glob√°ln√≠ funkce nebyly nalezeny. <-- FORM SPOT GRAF -- Prepare tomorrow chart data");
}

return msg;

//*****************************************************************************
//
// File Name        : 'Node_Graf_Spot'
// Title            : Node pro vypoƒçet grafu
// Author           : http://www.prochazka.zde.cz -> hacesoft 2024
// Created          : 14-07-2024, 11:00
// Revised          : 14-07-2024, 12:35
// Version          : 1.11
// Target Platform  : Node-RED
//
// This code is distributed under the GNU Public License
// V≈°echny informace jsou zahrnuty pod GPL licenci, pokud nen√≠ explicitnƒõ uveden jin√Ω typ licence.
// Pou≈æ√≠v√°n√≠ tƒõchto str√°nek a produkt≈Ø ke komerƒçn√≠m √∫ƒçel≈Øm lze jen se souhlasem autora.
// V≈°echna pr√°va vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//definice globaln√≠ch funkci
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; //dotaz na data z globalni strukturu

if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier) {
    if("todayHourlyPrices" in msg.payload) { 
        var line1 = [];
        var line2 = [];
        var currentTimeLine = [];
        const currentTime = new Date();
        const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); // prida CZ lokaci
        const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate)); // upravi stavajici objekt cas na prijatelnou podobu pro porovnavani

        var hourlyPrices = msg.payload.todayHourlyPrices;
        var currPrice = hourlyPrices[oDate_CZ.getHours()];
        var minPrice = Math.min(...hourlyPrices);
        var maxPrice = Math.max(...hourlyPrices);

        for(var i=0; i<24;)
        {
            // spot price
            line1[i] = { "x": new Date(oDate_CZ.setUTCHours(i,0,0)), "y": hourlyPrices[i] };
            // spot treshold
            line2[i] = { "x": new Date(oDate_CZ.setUTCHours(i,0,0)), "y": spotTresholdPrice };

            if (debug){
            //   node.warn("oDate_CZ: " + oDate_CZ);
            //   node.warn("i: " + i);
            //   node.warn("hourlyPrices[i] : " + hourlyPrices[i] );
            }
            i++;
        }   
        // add one more point to finish chart line
        line1[24] = { "x": new Date(oDate_CZ.setUTCHours(23,59,59)), "y": hourlyPrices[23] };
        line2[24] = { "x": new Date(oDate_CZ.setUTCHours(23,59,59)), "y": spotTresholdPrice };


        // Z√≠sk√°n√≠ ƒçasov√©ho posunu mezi lok√°ln√≠m ƒçasem a UTC v minut√°ch
        // V√Ωpoƒçet ƒçasov√©ho posunu v hodin√°ch: Pou≈æit√≠m metody getTimezoneOffset na aktu√°ln√≠m datu currentTime zjist√≠me ƒçasov√Ω posun mezi 
        // lok√°ln√≠m ƒçasem a UTC. Tento posun je v minut√°ch, tak≈æe jej p≈ôevedeme na hodiny a n√°sob√≠me -1, abychom z√≠skali obr√°cen√Ω posun.
        // √öprava aktu√°ln√≠ho ƒçasu: K aktu√°ln√≠mu ƒçasu currentTime p≈ôiƒç√≠t√°me(nebo odeƒç√≠t√°me) ƒçasov√Ω posun v 
        // milisekund√°ch(timezoneOffsetHours * 60 * 60 * 1000), abychom z√≠skali ƒças v UTC.
        var timezoneOffsetHours = -currentTime.getTimezoneOffset() / 60;
        var adjustedCurrentTime = new Date(currentTime.getTime() + timezoneOffsetHours * 60 * 60 * 1000);

        if (debug) {
            node.warn("adjustedCurrentTime: " + adjustedCurrentTime);
            node.warn("sDate: " + sDate);
            node.warn("currPrice: " + currPrice);
            node.warn("ƒåasov√Ω posun (hodin): " + timezoneOffsetHours);
        }

        // current time line marker
        // ≈ò√°dky currentTimeLine[0], currentTimeLine[1] a currentTimeLine[2] slou≈æ√≠ k vytvo≈ôen√≠ ƒç√°ry reprezentuj√≠c√≠ aktu√°ln√≠ ƒças na grafu. 
        // Tyto t≈ôi body urƒçuj√≠ pozici a v√Ω≈°ku ƒç√°ry:

        // X: Aktu√°ln√≠ ƒças(currentDate).
        // Y: Aktu√°ln√≠ cena(currPrice).
        // T√≠mto bodem zaƒç√≠n√° ƒç√°ra na grafu na √∫rovni aktu√°ln√≠ ceny.
        currentTimeLine[0] = { "x": adjustedCurrentTime, "y": currPrice };
        // X: Aktu√°ln√≠ ƒças (currentDate).
        // Y: Nejni≈æ≈°√≠ cena dne nebo spotov√° prahov√° cena minus 0, 1.
        // Tento bod urƒçuje spodn√≠ ƒç√°st ƒç√°ry, kter√° se nach√°z√≠ tƒõsnƒõ pod minim√°ln√≠ cenou dne nebo pod spotovou prahovou cenou.
        currentTimeLine[1] = { "x": adjustedCurrentTime, "y": ((minPrice < spotTresholdPrice) ? minPrice : spotTresholdPrice) - 0.1 };
        // X: Aktu√°ln√≠ ƒças (currentDate).
        // Y: Nejvy≈°≈°√≠ cena dne nebo spotov√° prahov√° cena plus 0, 1.
        // Tento bod urƒçuje horn√≠ ƒç√°st ƒç√°ry, kter√° se nach√°z√≠ tƒõsnƒõ nad maxim√°ln√≠ cenou dne nebo nad spotovou prahovou cenou.
        currentTimeLine[2] = { "x": adjustedCurrentTime, "y": ((maxPrice > spotTresholdPrice) ? maxPrice : spotTresholdPrice) + 0.1 };


        msg.payload = [{
            "series": ["Kƒç/kWh", "Spot limit", "Aktu√°lnƒõ"],
            "data": [line1, line2, currentTimeLine]
        }];

        msg.minmax = minPrice.toFixed(2) + " | " + maxPrice.toFixed(2);
    }
    else{// clear chart data to reset, data not available
        msg.payload = [];
    }
} else {// nenalezeny globalni funkce
    node.error("Glob√°ln√≠ funkce nebyly nalezeny. <-- FORM SPOT GRAF -- Prepare today chart data");
}

return msg;

// reset chart data
msg.minmax = "---";
msg.payload = [];
return msg;

// reset data
msg.fcolor = "grey";
msg.enabled = flow.get("isSpotAutoCtrlEnabled");
msg.payload = {"currentPrice": "---"};

return msg;

