global.set('getConfigProperty', function (propertyPath) {
    let config = global.get("config") || {};
    return propertyPath.split('.').reduce((o, p) => (o ? o[p] : undefined), config);
});

//*****************************************************************************
//ulozi data do globalniho obektu
//priklad: setConfigProperty("connectorConfig.tcpHost", "192.168.50.250");
global.set('setConfigProperty',function (propertyPath, value) {
    let config = global.get("config") || {};
    let schema = config;  // a moving reference to internal objects within config
    let pList = propertyPath.split('.');
    let len = pList.length;
    for(let i = 0; i < len-1; i++) {
        let elem = pList[i];
        if( !schema[elem] ) schema[elem] = {};
        schema = schema[elem];
    }

    schema[pList[len-1]] = value;
    global.set("config", config);
});

