//*****************************************************************************
function fSetFixedDate(){
    let hFIXDate = new Date();
    let hFixedDate = new Date(
        hFIXDate.getUTCFullYear(), // rok
        hFIXDate.getUTCMonth(), // mƒõs√≠c (od 0 do 11)
        hFIXDate.getUTCDate(), // den
        0, // hodina
        0, // minuta
        0, // sekunda
        0 // milisekunda
    );
    return hFixedDate;
}

//*****************************************************************************
function fSetFixedDateA() {
    let hFIXDate = new Date();
    let hFixedDate = new Date(
        hFIXDate.getFullYear(), // rok (lok√°ln√≠)
        hFIXDate.getMonth(), // mƒõs√≠c (lok√°ln√≠)
        hFIXDate.getDate(), // den (lok√°ln√≠)
        0, // hodina
        0, // minuta
        0, // sekunda
        0 // milisekunda
    );
    return hFixedDate;
}
