// File Name        : N/A
// Title            : N/A
// Author           : http://www.prochazka.zde.cz -> hacesoft 2025
// Created          : 03-08-2024, 21:19
// Revised          : 09-02-2025, 15:38
// Version          : 1.6
// Target Platform  : Node-RED
//
// This code is distributed under the GNU Public License
// V≈°echny informace jsou zahrnuty pod GPL licenci, pokud nen√≠ explicitnƒõ uveden jin√Ω typ licence.
// Pou≈æ√≠v√°n√≠ tƒõchto str√°nek a produkt≈Ø ke komerƒçn√≠m √∫ƒçel≈Øm lze jen se souhlasem autora.
// V≈°echna pr√°va vyhrazena (c) 1997 - 2025 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

// Definice glob√°ln√≠ch funkc√≠
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');
const getStartOfDayTimestamp = global.get('getStartOfDayTimestamp');
const getEndOfDayTimestamp = global.get('getEndOfDayTimestamp');
const intervalType = global.get('intervalType');
const fWrite_LogFile = global.get('fWrite_LogFile');

// Z√≠sk√°n√≠ pot≈ôebn√Ωch hodnot z glob√°ln√≠ho kontextu
const nVRM_site_id = fGetConfigProperty("nVRM_site_id") || 0;
//const bearerToken = fGetConfigProperty("bearerToken") || 0;
const deposit_Token = fGetConfigProperty("deposit_Token") || 0;


// Z√≠sk√°n√≠ ƒçasov√Ωch znaƒçek pro zaƒç√°tek a konec dne
const tStart = getStartOfDayTimestamp();
const tStop = getEndOfDayTimestamp();

// Nastaven√≠ hlaviƒçek po≈æadavku
msg.headers = {
    "Content-Type": "application/json",
    "x-authorization": `Token ${deposit_Token}`
};

// Nastaven√≠ parametr≈Ø dotazu
const params = {
    start: tStart,
    end: tStop,
    type: "live_feed",
    interval: intervalType,
};

// Nastaven√≠ payloadu
msg.payload = params;

// Nastaven√≠ URL pro API po≈æadavek
msg.url = `https://vrmapi.victronenergy.com/v2/installations/${nVRM_site_id}/stats?${serializeParams(params)}`;

// Inicializace druh√©ho v√Ωstupu (default je false)
let secondOutput = { live_feed: false };

// Kontrola platnosti tokenu
if (deposit_Token == "VRM_TOKEN" || nVRM_site_id == 0 || deposit_Token == 0) {
    // Kdy≈æ je token neplatn√Ω, poslat true na druh√Ω v√Ωstup
    secondOutput.live_feed = true;
    // Debugging
    if (debug) {
        //node.warn("neni token: " + bearerToken);
        fWrite_LogFile("neni token: *************************************** ", deposit_Token);
        //fWrite_LogFile("msg.url: ", msg.url);
        //fWrite_LogFile("nVRM_site_id: ", nVRM_site_id);
    }
}

// Debugging
if (debug) {
    //node.warn("bearerToken: " + bearerToken);
    //fWrite_LogFile("bearerToken: ", bearerToken);
    //fWrite_LogFile("msg.url: ", msg.url);
}

// Funkce pro serializaci objektu parametr≈Ø do URL query stringu
function serializeParams(params) {
    return Object.keys(params)
        .map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key])}`)
        .join('&');
}

// Vr√°tit pole zpr√°v pro oba v√Ωstupy
return [msg, secondOutput];

