//*****************************************************************************

const debug = false;  // Debugging flag

// Base64 obr√°zek pro vyb√≠jen√≠
const imageBase64Vybijeni = "data:image/png;base64,";

// Base64 obr√°zek pro nab√≠jen√≠
const imageBase64Nabijeni = "data:image/png;base64,";

// Glob√°ln√≠ funkce
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');

// Naƒçten√≠ aktu√°ln√≠ch hodnot
let SOC_current = global.get("nBattery_State_Charge") || 50;  // SOC v %
let SOC_previous = global.get("SOC_previous") || SOC_current;  // Minul√Ω SOC (%)
let SOC_cumulative = global.get("SOC_cumulative") || 0;  // Kumulativn√≠ SOC pro dan√Ω smƒõr

// Sledov√°n√≠ posledn√≠ho smƒõru SOC
let last_SOC_direction = global.get("last_SOC_direction") || null;

// Naƒçten√≠ p≈ôijat√© a odebran√© energie
let nBattery_Power = global.get("nBattery_Power") || 0; // V√Ωkon baterie (W)
let nBattery_input_Wh = global.get("nBattery_input_Wh") || 0;
let nBattery_output_Wh = global.get("nBattery_output_Wh") || 0;

// P≈ôeveden√≠ v√Ωkonu na kladn√©/z√°porn√© ƒç√≠slo
let nSignet_Battery_Power = nConvertSignetUnsignet(nBattery_Power);

// V√Ωpoƒçet p≈ôijat√© a odebran√© energie
if (nSignet_Battery_Power > 0) {
    nBattery_input_Wh += nSignet_Battery_Power / 3600;  // P≈ôijat√° energie do baterie
} else {
    nBattery_output_Wh += Math.abs(nSignet_Battery_Power) / 3600;  // Odebran√° energie z baterie
}

// V√Ωpoƒçet zmƒõny SOC
let delta_SOC = SOC_current - SOC_previous;
let current_SOC_direction = delta_SOC > 0 ? "charge" : delta_SOC < 0 ? "discharge" : last_SOC_direction;

// Pokud se zmƒõnil smƒõr SOC (z nab√≠jen√≠ na vyb√≠jen√≠ nebo opaƒçnƒõ), uzav≈ôeme p≈ôedchoz√≠ v√Ωpoƒçet
if (current_SOC_direction !== last_SOC_direction && last_SOC_direction !== null) {
    if (last_SOC_direction === "charge" && nBattery_input_Wh > 0) {
        let charge_efficiency = nBattery_input_Wh > 0 ? (SOC_cumulative / nBattery_input_Wh) * 100 : 0;
        global.set("last_charge_efficiency", charge_efficiency.toFixed(2));
    }
    if (last_SOC_direction === "discharge" && nBattery_output_Wh > 0) {
        let discharge_efficiency = Math.abs(SOC_cumulative) > 0 ? (nBattery_output_Wh / Math.abs(SOC_cumulative)) * 100 : 0;
        global.set("last_discharge_efficiency", discharge_efficiency.toFixed(2));
    }

    // Reset SOC kumulace p≈ôi zmƒõnƒõ smƒõru
    SOC_cumulative = 0;
    //nBattery_input_Wh = 0;
    //nBattery_output_Wh = 0;
}

// Kumulace SOC pro aktu√°ln√≠ smƒõr
SOC_cumulative += delta_SOC;

// Ulo≈æen√≠ nov√Ωch hodnot do global contextu
global.set("nBattery_input_Wh", nBattery_input_Wh);
global.set("nBattery_output_Wh", nBattery_output_Wh);
global.set("SOC_previous", SOC_current);
global.set("SOC_cumulative", SOC_cumulative);
global.set("last_SOC_direction", current_SOC_direction);

// Naƒçten√≠ posledn√≠ch vypoƒçten√Ωch efektivit
let charge_efficiency = global.get("last_charge_efficiency") || 0;
let discharge_efficiency = global.get("last_discharge_efficiency") || 0;

// P≈ôeveƒète hodnoty na ƒç√≠sla, pokud jsou ulo≈æeny jako ≈ôetƒõzce
charge_efficiency = Number(charge_efficiency);
discharge_efficiency = Number(discharge_efficiency);

// P≈ôid√°n√≠ ikon do zpr√°v
msg.tDischarge_efficiency = `<img src="${imageBase64Vybijeni}"> ${discharge_efficiency.toFixed(1)}% v√Ωdej`;
msg.tCharge_efficiency = `<img src="${imageBase64Nabijeni}"> ${charge_efficiency.toFixed(1)}% p≈ô√≠jem`;

// Debugging zpr√°vy
if (debug) {
    node.warn(`SOC_current: ${SOC_current}%, SOC_previous: ${SOC_previous}%, ŒîSOC: ${delta_SOC}%`);
    node.warn(`P≈ôijat√° energie: ${nBattery_input_Wh} Wh, Odebran√° energie: ${nBattery_output_Wh} Wh`);
    node.warn(`Efektivita nab√≠jen√≠: ${charge_efficiency}%, Efektivita vyb√≠jen√≠: ${discharge_efficiency}%`);
    node.warn(`Aktu√°ln√≠ smƒõr SOC: ${current_SOC_direction}, Kumulovan√Ω SOC: ${SOC_cumulative}`);
}

// V√Ωstupn√≠ zpr√°va
msg.payload = {
    SOC_current,
    SOC_previous,
    delta_SOC,
    SOC_cumulative,
    nBattery_input_Wh: nBattery_input_Wh.toFixed(2),
    nBattery_output_Wh: nBattery_output_Wh.toFixed(2),
    current_SOC_direction
};

return msg;

