// Pou≈æ√≠v√°n√≠ tƒõchto str√°nek a produkt≈Ø ke komerƒçn√≠m √∫ƒçel≈Øm lze jen se souhlasem autora.
// V≈°echna pr√°va vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v
const nCountTimeStamp = 4; //kolik cyklu se m√° provest nez bude uznane platne casove razitko.
let nStatusIndicator = "";
let modbusResponse = msg.payload;

// Z√≠sk√°n√≠ poƒçtu p≈ôijat√Ωch ƒçasov√Ωch raz√≠tek z flow contextu
let timestampCounter = flow.get("timestampCounter") || 0;

const nCode = isTimestamp(modbusResponse);

if (nCode == true) {
    // Pokud je hodnota platn√Ωm ƒçasov√Ωm raz√≠tkem, zvy≈°te ƒç√≠taƒç
    timestampCounter += 1;

    // Pokud ƒç√≠taƒç dos√°hne hodnoty nCountTimeStamp, resetuje modbusResponse a ƒç√≠taƒç
    if (timestampCounter >= nCountTimeStamp) {
        modbusResponse = 0;  // Platn√© ƒçasov√© raz√≠tko 10x za sebou
        timestampCounter = nCountTimeStamp;  // Resetov√°n√≠ ƒç√≠taƒçe
    }
} else {
    // Pokud nep≈ôijde platn√© ƒçasov√© raz√≠tko, ƒç√≠taƒç se vynuluje
    timestampCounter = 0;
}

// Ulo≈æen√≠ aktu√°ln√≠ho stavu ƒç√≠taƒçe do flow contextu
flow.set("timestampCounter", timestampCounter);

if (modbusResponse != 0){
    nStatusIndicator ="on"
} else {
    nStatusIndicator ="off";
}  
const indicatorMsg = { indicator: '_tcp_connect', status: nStatusIndicator, topic: 'update-indicator' };
return { payload: indicatorMsg };


// Funkce pro zji≈°tƒõn√≠, zda je hodnota ƒçasov√© raz√≠tko
function isTimestamp(value) {
    if (typeof value !== 'number' || isNaN(value)) {
        return false;
    }

    const now = Date.now();
    const minTimestamp = new Date('1970-01-01').getTime(); // UNIX epoch start
    const maxTimestamp = new Date('2100-01-01').getTime(); // Future limit

    // Check if it's a millisecond timestamp
    if (value >= minTimestamp && value <= maxTimestamp) {
        return true;
    }

    // Check if it's a second timestamp (divide by 1000)
    if (value * 1000 >= minTimestamp && value * 1000 <= maxTimestamp) {
        return true;
    }

    return false;
}

