// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//const filename = global.get("filename");
const defaultConfig = global.get("defaultConfig");
let msg1 = null;

// P≈ô√≠klad v√Ωstupu: "drwxrwxr-x 2 username group 4096 Oct 27 12:00 /cesta/k/adresari"
const output = msg.payload.trim();
const permissions = output.split(" ")[0];

// Kontrola pr√°v
const isReadable = permissions[1] === "r";
const isWritable = permissions[2] === "w";

msg.payload = {
    path: msg.filename,
    readable: isReadable,
    writable: isWritable,
    permissions: permissions
};
if (isReadable == true || isWritable == isWritable){//do adresa≈ôe se da zapisovat
    msg1 = { sTry_File: msg.filename + defaultConfig };
    global.set("sValid_Patch", msg.filename);

    return [msg, msg1];
}
else return [msg, null]; // Nejsou prava pro zapis a nebo ƒçten√≠ z dan√© cesty.

