// Funkce na odstranƒõn√≠ n√°zvu souboru z cesty
const removeFilename = (path) => {
    if (typeof path === "string" && path.includes('/')) {
        return path.substring(0, path.lastIndexOf('/')); // Odstran√≠ v≈°e po posledn√≠m '/'
    }
    return null;
};

