// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

//definice globaln√≠ch funkci
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

// Nacte token z msg.payload a sestav√≠ po≈æadavek
const deposit_Token = msg.payload.token;

// Debugging
if (debug) {
    node.warn("deposot_Token: " + deposit_Token);
}
fSetConfigProperty("deposit_Token", deposit_Token); //ulozit data do globalni struktury
msg.deposit_Token = deposit_Token;

return msg;

// Ovƒõ≈ô√≠me, ≈æe msg.payload je ƒç√≠slo
if (!isNaN(msg.payload)) {
    // P≈ôevod na ƒç√≠slo (pro p≈ô√≠pad, ≈æe vstup je string)
    let decimalNumber = parseInt(msg.payload, 10);

    // P≈ôevod na hexadecim√°ln√≠ ≈ôetƒõzec
    let hexString = decimalNumber.toString(16).toUpperCase();

    // Doplnƒõn√≠ nul na d√©lku 4 znaky
    hexString = hexString.padStart(4, '0');

    // P≈ôid√°n√≠ prefixu 0x
    msg.payload = `0x${hexString}`;
} else {
    // Pokud nen√≠ ƒç√≠slo, vr√°t√≠me chybu
    msg.payload = "Neplatn√Ω vstup";
}

return msg;

// Ovƒõ≈ô√≠me, ≈æe msg.payload je pole
if (Array.isArray(msg.payload)) {
    // Zjist√≠me skuteƒçnou velikost pole (poƒçet platn√Ωch prvk≈Ø)
    let actualSize = msg.payload.length;

    // P≈ôevedeme v≈°echna des√≠tkov√° ƒç√≠sla na ≈°estn√°ctkov√°
    let hexArray = msg.payload.map(function(num) {
        if (!isNaN(num)) {
            // P≈ôevod na ƒç√≠slo (pro p≈ô√≠pad, ≈æe vstup je string)
            let decimalNumber = parseInt(num, 10);

            // P≈ôevod na hexadecim√°ln√≠ ≈ôetƒõzec
            let hexString = decimalNumber.toString(16).toUpperCase();

            // Doplnƒõn√≠ nul na d√©lku 4 znaky
            hexString = hexString.padStart(4, '0');

            // P≈ôid√°n√≠ prefixu 0x
            return `0x${hexString}`;
        } else {
            // Pokud nen√≠ ƒç√≠slo, vr√°t√≠me chybu
            return "Neplatn√Ω vstup";
        }
    });

    // Vytvo≈ô√≠me v√Ωstupn√≠ zpr√°vu
    let outputMsg = {
        payload: hexArray,
        actualSize: actualSize
    };

    // Po≈°leme v√Ωstupn√≠ zpr√°vu d√°l
    return outputMsg;
} else {
    // Pokud vstup nen√≠ pole, vr√°t√≠me chybu
    msg.payload = "Neplatn√Ω vstup";
    return msg;
}
