//*****************************************************************************
//
// File Name        : N/A
// Title            : AC LOAD
// Author           : http://www.prochazka.zde.cz
// Created          : 20-06-2024, 11:00
// Revised          : 02-04-2025, 18:35
// Version          : 1.32
// Target Platform  : Node-RED
//
// This code is distributed under the GNU Public License
// VÅ¡echny informace jsou zahrnuty pod GPL licenci, pokud nenÃ­ explicitnÄ› uveden jinÃ½ typ licence.
// PouÅ¾Ã­vÃ¡nÃ­ tÄ›chto strÃ¡nek a produktÅ¯ ke komerÄnÃ­m ÃºÄelÅ¯m lze jen se souhlasem autora.
// VÅ¡echna prÃ¡va vyhrazena (c) 1997 - 2025 hacesoft.
//
//*****************************************************************************
// com.victronenergy.system mÃ¡ ID 100 (Cerbo)
// com.victronenergy.battery mÃ¡ ID 100(Lynx Shunt)
// com.victronenergy.vebus mÃ¡ ID 227(MP - II)
// com.victronenergy.solarcharger mÃ¡ v pÅ™Ã­padÄ› prvnÃ­ho MPPT ID 100, dalÅ¡Ã­ potom ID 1 a 2(jÃ¡ mÃ¡m tÅ™i MPPT)
// com.victronenergy.grid mÃ¡ 31(elektromÄ›r EM540 pÅ™es USB)

// Debugging flag
const debug = false;  // Nastavte na true pro zapnutÃ­ debug zprÃ¡v

// Definice konstanty pro oÄekÃ¡vanÃ½ poÄet prvkÅ¯
const EXPECTED_ARRAY_LENGTH = 24;

//definice globalnÃ­ch funkci
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');
const mExtractTime = global.get('mExtractTime');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');
const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; //dotaz na data z globalni strukturu


try {
    if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier && nConvertSignetUnsignet && mExtractTime && fGetConfigProperty && fSetConfigProperty) {
        let nSet_Grid_Point = 0;

        // Definice fazi AC LOAD
        //Nejprve nacte data z MODBUSu, pokud je neplatna hodnota, tak neÄte poslednÃ­ znÃ¡mou hodnotu a pokud i ta neexistuje, tak vysledek je hodnota 0.
        const phase1 = (parseFloat(msg.payload.phase1) || global.get('phase1')) ?? 0; // Hodnota z registru 817
        const phase2 = (parseFloat(msg.payload.phase2) || global.get('phase2')) ?? 0;  // Hodnota z registru 818
        const phase3 = (parseFloat(msg.payload.phase3) || global.get('phase3')) ?? 0;  // Hodnota z registru 819

        // Definice Gridu
        //Nejprve nacte data z MODBUSu, pokud je neplatna hodnota, tak neÄte poslednÃ­ znÃ¡mou hodnotu a pokud i ta neexistuje, tak vysledek je hodnota 0.
        const grid1 = fCheckValue(nConvertSignetUnsignet(parseFloat(msg.payload.grid1)), 10, "grid1"); // Hodnota z registru 811
        const grid2 = fCheckValue(nConvertSignetUnsignet(parseFloat(msg.payload.grid2)), 10, "grid2"); // Hodnota z registru 812
        const grid3 = fCheckValue(nConvertSignetUnsignet(parseFloat(msg.payload.grid3)), 10, "grid3"); // Hodnota z registru 813

        // Definice vyroby FV panelu
        //Nejprve nacte data z MODBUSu, pokud je neplatna hodnota, tak neÄte poslednÃ­ znÃ¡mou hodnotu a pokud i ta neexistuje, tak vysledek je hodnota 0.
        const pvPower = fCheckValue(parseFloat(msg.payload.totalPVPower), 10, "pvPower"); // Hodnota z registru 819

        // Definice stavu nabiti baterie
        //Nejprve nacte data z MODBUSu, pokud je neplatna hodnota, tak neÄte poslednÃ­ znÃ¡mou hodnotu a pokud i ta neexistuje, tak vysledek je hodnota 0.
        const nBattery_State_Charge = (parseFloat(msg.payload.nBattery_State_Charge) || global.get('nBattery_State_Charge')) ?? 0;  // Hodnota z registru 843 v %//SOC
        const nBattery_Power = (parseFloat(msg.payload.nBattery_Power) || global.get('nBattery_Power')) ?? 0; // Battery Power (System) 842 ve W
        const nBattery_Current = (parseFloat(msg.payload.nBattery_Current) || global.get('nBattery_Current')) ?? 0; // Battery Power (System) 841 v A DC
        const nBattery_Voltage = (parseFloat(msg.payload.nBattery_Voltage) || global.get('nBattery_Voltage')) ?? 0; // Battery Power (System) 840 v V DC
        const nESS_BatteryLife_SoC_limit = (parseFloat(msg.payload.nESS_BatteryLife_SoC_limit) || global.get('nESS_BatteryLife_SoC_limit')) ?? 0; // ESS BatteryLife SoC limit (read only) 2903 v % 

        // Definice GET_Grid_Feed_2706 - ZaokrouhlenÃ­ dolÅ¯	
        const nGET_Grid_Feed_2706 = Math.floor((parseFloat(msg.payload.nGrid_Feed_2706)) || global.get('nGET_Grid_Feed_2706'))*100 ?? 0;  // Hodnota z registru 2706 v W
        //Maximum System Grid Feed In:  -1: No limit, >=0: limited system feed-in. Applies to DC-coupled and AC-coupled feed-in.

        // Pole dnesniho spotu
        const todayHourlyPrices = global.get("todayHourlyPrices");

        // Ulozeni dat do globalnich promennych, ktere jsou dostupne mezi flow
        global.set("phase1", phase1);
        global.set("phase2", phase2);
        global.set("phase3", phase3);
        global.set("pvPower", pvPower);
        global.set("nBattery_State_Charge", nBattery_State_Charge);
        global.set("nESS_BatteryLife_SoC_limit", nESS_BatteryLife_SoC_limit);
        global.set("grid1", grid1);
        global.set("grid2", grid2);
        global.set("grid3", grid3);
        global.set("nBattery_Power", nBattery_Power);
        global.set("nBattery_Current", nBattery_Current);
        global.set("nBattery_Voltage", nBattery_Voltage);
        global.set("nGET_Grid_Feed_2706", nGET_Grid_Feed_2706);


        // NaÄteni globalnich dat
        const nBalancingReserve = fGetConfigProperty("nBalancingReserve") || 0; // Hodnota ve W. SlouÅ¾Ã­ k vyrovnÃ¡vÃ¡nÃ­ vÃ½konu, aby se zamezilo kolÃ­sÃ¡nÃ­.
        const nGridConsumptionEnable = !!fGetConfigProperty("nGridConsumptionEnable"); // true znamena zapnut pÅ™epinaÄ NUCENY ODBER Z GRIDu
        const nSetGridValue = fGetConfigProperty("nSetGridValue") || 0; // nastavevi v UI SetPoint
        //const isSpotAutoCtrlEnabled = !!fGetConfigProperty("isSpotAutoCtrlEnabled"); // true znamenÃ¡ Automaticky podle SPOTu
        const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; // Limitni cena
        const switchDelayCharging = !!fGetConfigProperty("switchDelayCharging") || 0; // true znamena ze je aktivovana funkce posunuti nabijeni baterie
        //const tDefaultTimeStart = fGetConfigProperty("tDefaultTimeStart") || 0; // Defaultni cas zacatku posunuti nabijeni beterie
        const tStartBattery = fGetConfigProperty("tStartBattery") || 0; // Zadany cas start posunu nabijeni beterie
        const tStopBattery = fGetConfigProperty("tStopBattery") || 0; // Zadany cas stop posunu nabijeni beterie
        const nMorningPeakBatterySales = !!fGetConfigProperty("nMorningPeakBatterySales") || 0; // Switch Prodej baterie v rannÃ­ Å¡piÄce
        const nEveningPeakBatterySales = !!fGetConfigProperty("nEveningPeakBatterySales") || 0; // Switch Prodej baterie ve veÄernÃ­ Å¡piÄce
        const nMorningSOC_sales = fGetConfigProperty("nMorningSOC_sales") || 0; // Procenta - Prodej Rano do SOC
        const nEveningSOC_sales = fGetConfigProperty("nEveningSOC_sales") || 0; // Procenta - Prodej VeÄer do SOC
        const nMAX_Grid_Point = fGetConfigProperty("nMAX_Grid_Point") || 0; // MaximalnÃ­ vybijecÃ­ proud ve W
        const sGridChargingSwitch = !!fGetConfigProperty("sGridChargingSwitch") || 0; // Switch Nabijeni beterie z Gridu
        const nGridChargingSOC = fGetConfigProperty("nGridChargingSOC") || 0; // Procenta - Nabiti baterie z GRIDU do SOC
        // Switch pro inekrÃ¡Å¾ ele.i z FW panelÅ¯. Energii dodÃ¡vÃ¡ na GRID jen tehdy, kdyÅ¾ je dostatek z pamenu
        // Minus uÅ¾iteÄnÃ¡ zÃ¡tÄ›Å¾, a do hodnory nastavenÃ© v AcPowerSetPoint (nSetGridValue). 
        // Funkce je proprietnÃ­, nepouÅ¾Ã­vat v jinÃ­ch instalaci, pokud nevÃ­te co dÃ©lÃ¡. 
        const nEnergyThresholdInjector = !!fGetConfigProperty("nEnergyThresholdInjector") || 0; 

        const currentPrice = global.get("currentPrice") || 0; // Aktualni SPOT cena
        const nPretoky = !!global.get("nPretoky") || 0; // Pretoky povoleny- true, zakazany - false


        const currentTime = new Date(); // aktualni cas
        const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); // prida CZ lokaci

        const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate)); // upravi stavajici obekt cas na prijatelnou podobu pro porovnavani

        const tStartBatteryTime = mExtractTime(tStartBattery); // vstupni promenna je jen milisekundy a nejde porovnavat s aktualnim casem, tak opet uprava na prijatelnou podobu pro porovnavani.
        const tStopBatteryTime = mExtractTime(tStopBattery);

        // SpoÄÃ­tat celkovÃ½ odbÄ›r AC zÃ¡tÄ›Å¾e
        let totalACLoad = phase1 + phase2 + phase3;


        //Zjisti skuteÄnou dÃ©lku pole, standartnÄ› je 24 prvkÅ¯.
        const arrayLengthPrices = checkArrayLength(todayHourlyPrices, EXPECTED_ARRAY_LENGTH);
        // Hleda ranni a odpoleni peaky
        // prvni promenna je pole aktualniho spot a druha promenna rika jak dlouhy peak muse bejt
        const morningPeak1 = findMorningPeakHours(todayHourlyPrices, arrayLengthPrices, 2);
        const afternoonPeak1 = findAfternoonPeakHours(todayHourlyPrices, arrayLengthPrices, 2);


        // Debugging
        if (debug) {
            //node.warn("global.get(todayHourlyPrices): " + todayHourlyPrices);
            //node.warn("global.get(morningPeak1): " + morningPeak1);
            //node.warn("global.get(afternoonPeak1): " + afternoonPeak1);
            //node.warn("nSetGridValue: " + nSetGridValue);
            //node.warn("nGridConsumptionEnable: " + nGridConsumptionEnable);
            //node.warn("oDate_CZ: " + oDate_CZ);
            //node.warn("nSet_Grid_Point: " + nSet_Grid_Point);
            node.warn("nGET_Grid_Feed_2706: " + nGET_Grid_Feed_2706);
        }

        let nZbytekFV = pvPower - (totalACLoad + nBalancingReserve);
        nSet_Grid_Point = fSet_Grid(nGridConsumptionEnable, nSetGridValue, currentPrice);    

        //BLOCK
        if (!sGridChargingSwitch){// neni zapnuta funkce na nabijeni baterie z GRIDu
            if (nPretoky == true) { // povolene pretoky

                //Funkce inektaÅ¾ na GRID podle vÃ½konu FV ÃºanelÅ¯
                if (nEnergyThresholdInjector == true) {//true -> zapnutÃ¡ inektaÅ¾ na GRID podle vÃ½konu FV. 
                    if (currentPrice > 0) {// Dokud neni zapornÃ½ spot
                        if (nZbytekFV > 0 && nSetGridValue < 0) {//Zbytek je vetsÃ­ nez nastaena hodnota.
                           //Tato podmÃ­nka kontroluje dvÄ› vÄ›ci:
                                //nZbytekFV je vÄ›tÅ¡Ã­ neÅ¾ nula, coÅ¾ znamenÃ¡, Å¾e je to kladnÃ¡ hodnota.
                                //nSetGridValue je menÅ¡Ã­ neÅ¾ nula, coÅ¾ znamenÃ¡, Å¾e je to zÃ¡pornÃ¡ hodnota.
                           //node.warn("nZbytekFV: " + nZbytekFV);
                            //node.warn("nSetGridValue: " + nSetGridValue);
                            nSet_Grid_Point = nSetGridValue;  //nastav nastavenou hodnotu na nSet_Grid_Point.
                        }
                    }
                }

                //Funkce na posunutÃ­ nabÃ­jenÃ­ baterie
                if (switchDelayCharging == true) { // true znamena ze je aktivovana funkce posunuti nabijeni baterie
                    if (tStartBatteryTime && tStopBatteryTime) {
                        if (oDate_CZ >= tStartBatteryTime && oDate_CZ <= tStopBatteryTime) { // AktuÃ¡lnÃ­ Äas je v rozsahu
                            if (nZbytekFV > 0) { // Prebytek FV je kladna hodnota
                                nSet_Grid_Point = -nZbytekFV; // prevest na zapornou hodnotu
                            } else { // mala vyroba z FV panelu
                                if (currentPrice > 0) {// Dokud neni zapornÃ½ spot
                                    //nedelej nic s registrem nSet_Grid_Point - regist je nastaven pomoci funkce fSet_Grid, dokud ji jine nastaveni nezmeni
                                } else {nSet_Grid_Point = 0;}// vynuluj registr
                            }
                        } else { // AktuÃ¡lnÃ­ Äas je mimo rozsah
                            // nSet_Grid_Point = fSet_Grid(nGridConsumptionEnable, nSetGridValue, isSpotAutoCtrlEnabled, spotTresholdPrice, currentPrice, nPretoky, nMAX_Grid_Point);
                        }
                    } else { // ÄŒas zaÄÃ¡tku nebo konce nenÃ­ nastaven
                        nSet_Grid_Point = 0;
                    }
                }
            }

            // funkce vybitÃ­ baterie pÅ™i rannÃ­ Å¡piÄce
            if (nMorningPeakBatterySales) {
                if (morningPeak1.length > 0) {
                    const peakTimeStart = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), morningPeak1[0], 0, 0, 0);
                    const peakTimeEnd = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), morningPeak1[morningPeak1.length - 1], 59, 0, 0);

                    if (debug) {
                        //node.warn("-----------------------------------------");
                        //node.warn("global.get(morningPeak1): " + morningPeak1);
                        //node.warn("peakTimeStart: " + peakTimeStart);
                        //node.warn("peakTimeEnd: " + peakTimeEnd);
                        //node.warn("oDate_CZ: " + oDate_CZ);
                        //node.warn("global.get(nBattery_State_Charge): " + nBattery_State_Charge);
                        //node.warn("nSet_Grid_Point: " + nSet_Grid_Point);
                    }

                    if (oDate_CZ >= peakTimeStart && oDate_CZ < peakTimeEnd) {
                        if (nBattery_State_Charge >= nMorningSOC_sales) {
                            if (nPretoky == true) { // povolene pretoky
                                if (currentPrice >= spotTresholdPrice) {// ProdÃ¡vat do nastavenÃ© hodnoty v spotTresholdPrice
                                    nSet_Grid_Point = EnsureNegativeValue(nMAX_Grid_Point);
                                }
                            }
                        }
                    }
                }
            }

            // funkce vybitÃ­ baterie pÅ™i veÄernÃ­  Å¡piÄce
            if (nEveningPeakBatterySales) {
                if (afternoonPeak1.length > 0) {
                    const peakTimeStart = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), afternoonPeak1[0], 0, 0, 0);
                    const peakTimeEnd = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), afternoonPeak1[afternoonPeak1.length - 1], 59, 0, 0);

                    if (debug) {
                        //node.warn("-----------------------------------------");
                        //node.warn("global.get(afternoonPeak1): " + afternoonPeak1);
                        //node.warn("peakTimeStart: " + peakTimeStart);
                        //node.warn("peakTimeEnd: " + peakTimeEnd);
                        //node.warn("oDate_CZ: " + oDate_CZ);
                        //node.warn("global.get(nBattery_State_Charge): " + nBattery_State_Charge);
                        //node.warn("nSet_Grid_Point: " + nSet_Grid_Point);
                    }

                    if (oDate_CZ >= peakTimeStart && oDate_CZ < peakTimeEnd) {
                        if (nBattery_State_Charge >= nEveningSOC_sales) {
                            if (nPretoky == true) { // povolene pretoky
                                if (currentPrice >= spotTresholdPrice) {// ProdÃ¡vat do nastavenÃ© hodnoty v spotTresholdPrice
                                    nSet_Grid_Point = EnsureNegativeValue(nMAX_Grid_Point);
                                }
                            }
                        }
                    }
                }
            }
        } else {// Zapnuta funkce nabijeni baterie z gridu, nabiji do nastaveneho SOC, pak vypne nabijenÃ­ :).
            //nGridChargingSOC
            if (nBattery_State_Charge < nGridChargingSOC) {
                nSet_Grid_Point = EnsurePositiveValue(nMAX_Grid_Point);
            }else {//vypnout nabijeni
            fSetConfigProperty("sGridChargingSwitch", false); //vypne nabijeni aby nebylo kminÃ¡nÃ­ (urdÅ™ovÃ¡nÃ­ baterie na SOC tato funkce neÅ™eÅ¡Ã­)
            }
            if (debug) {
                        //node.warn("-----------------------------------------");
                        node.warn("nBattery_State_Charge: " + nBattery_State_Charge);
                        node.warn("nGridChargingSOC: " + nGridChargingSOC);
                    }
        }

        // Nastavit celkovÃ½ odbÄ›r jako novou hodnotu pro nastavenÃ­ sÃ­tÄ›
        msg.payload = { "nSet_Grid_Point": nSet_Grid_Point,
                        "nGET_Grid_Feed_2706": nGET_Grid_Feed_2706,
                        "pvPower": pvPower,
                        "nMAX_Grid_Point": nMAX_Grid_Point,
                        "totalACLoad": totalACLoad,
                        "nZbytekFV": nZbytekFV
                        };

        } else {// nenalezeny globalni funkce
        node.error("GlobÃ¡lnÃ­ funkce nebyly nalezeny. <-- FORM BATTERY CONTROL-- AC LOAD");
    }
    return msg;
} catch (error) {
    node.error("Error in script: " + error.message);
}

// Oprava validnich dat z MBUSu, pokud bude X krat po sobe nula, tak vysledek bude nula, pokud bude nula 1x a min nez trigger, 
// tak se vezme hodnota z glovalniho buffru
function fCheckValue(pvPowerReading, readingCount, globalVarName) {
    // Dynamicky vytvoÅ™Ã­ nÃ¡zev ÄÃ­taÄe na zÃ¡kladÄ› globalVarName
    let zeroReadingsCountKey = `${globalVarName}_Count`;

    // ZÃ­skÃ¡ dynamicky ÄÃ­taÄ a poÄÃ¡teÄnÃ­ hodnotu vÃ½konu z globÃ¡lnÃ­ch promÄ›nnÃ½ch
    let zeroReadingsCount = global.get(zeroReadingsCountKey) || 0;
    let pvPower = global.get(globalVarName) ?? 0; // DefaultnÄ› nastavÃ­me na 0, pokud nenÃ­ dostupnÃ¡ globÃ¡lnÃ­ hodnota

    if (pvPowerReading === 0) {
        // ZvÃ½Å¡enÃ­ ÄÃ­taÄe, pokud byla pÅ™eÄtena hodnota 0
        zeroReadingsCount += 1;

        if (zeroReadingsCount >= readingCount) {
            // Pokud ÄÃ­taÄ dosÃ¡hne poÅ¾adovanÃ©ho poÄtu, nastavÃ­me pvPower na 0
            pvPower = 0;
            zeroReadingsCount = 0; // ResetovÃ¡nÃ­ ÄÃ­taÄe
        }
    } else if (pvPowerReading === null) {
        // Pokud je ÄtenÃ­ neplatnÃ©, pouÅ¾ijeme globÃ¡lnÃ­ promÄ›nnou
        zeroReadingsCount = 0; // Reset ÄÃ­taÄe, pokud ÄtenÃ­ nebylo 0
    } else {
        // Pokud je ÄtenÃ­ platnÃ© a nenulovÃ©, pouÅ¾Ã­vÃ¡me ho
        pvPower = pvPowerReading;
        zeroReadingsCount = 0; // Reset ÄÃ­taÄe, pokud ÄtenÃ­ nebylo 0
    }

    // UloÅ¾enÃ­ dynamicky generovanÃ©ho ÄÃ­taÄe zpÄ›t do globÃ¡lnÃ­ promÄ›nnÃ©
    global.set(zeroReadingsCountKey, zeroReadingsCount);
    // UloÅ¾enÃ­ dynamicky generovanÃ© hodnoty vÃ½konu zpÄ›t do globÃ¡lnÃ­ promÄ›nnÃ©
    global.set(globalVarName, pvPower);

    return pvPower;
}

/**
 * Funkce nastavuje GridPoint podle zadane hodnoty s omezenÃ­m maximÃ¡lnÃ­ho pÅ™etoku
 * @param {boolean} nGridConsumptionEnable - true znamenÃ¡ zapnutÃ½ pÅ™epÃ­naÄ NUCENÃ ODBÄšR Z GRIDu
 * @param {number} nSetGridValue - NastavenÃ¡ hodnota SetPoint v UI
 * @param {number} currentPrice - AktuÃ¡lnÃ­ SPOT cena
 * @returns {number} nSetGridPoint - VÃ½slednÃ¡ hodnota GridPointu
 */

function fSet_Grid(nGridConsumptionEnable, nSetGridValue, currentPrice) {
    let nSetGridPoint = 0;

    if (currentPrice > 0) { // Dokud nenÃ­ zÃ¡pornÃ½ spot
        if (nGridConsumptionEnable === true) { // Zapnut nucenÃ½ odbÄ›r z Gridu
            nSetGridPoint = nSetGridValue;
        }
    }
    return nSetGridPoint;
}

// Tato funkce vypoÄÃ­tÃ¡ mediÃ¡n zadanÃ½ch hodnot
function calculateMedian(values) {
    values = values.slice().sort((a, b) => a - b);
    const mid = Math.floor(values.length / 2);

    if (values.length % 2 === 0) {
        return (values[mid - 1] + values[mid]) / 2;
    } else {
        return values[mid];
    }
}


// Funkce pro vÃ½pis pole
function printArray(array) {
    return `[${array.join(', ')}]`;
}

// Funkce pro kontrolu poÄtu prvkÅ¯ v poli
function checkArrayLength(array, expectedLength) {
    const actualLength = array.length;
    if (debug) {
        if (actualLength !== expectedLength) {
            node.error(`Pole neobsahuje oÄekÃ¡vanÃ½ poÄet prvkÅ¯. OÄekÃ¡vÃ¡no: ${expectedLength}, obdrÅ¾eno: ${actualLength}`);
            node.error(`Obsah pole: ${printArray(array)}`);
        }
    }
    return actualLength;
}

// Tyto funkce pouÅ¾Ã­vajÃ­ calculateMedian k urÄenÃ­ prahovÃ© hodnoty pro rannÃ­ a odpolednÃ­ hodiny
// Funkce pro hledÃ¡nÃ­ rannÃ­ Å¡piÄky
function findMorningPeakHours(hourlyValues, arrayLength = EXPECTED_ARRAY_LENGTH, maxPeakLength = 3) {
    if (hourlyValues.length !== arrayLength) {
        throw new Error(`Function: findMorningPeakHours -> Array must contain exactly ${arrayLength} elements.`);
    }

    // VÃ½poÄet mediÃ¡nu pro prvnÃ­ polovinu dne
    const halfLength = Math.floor(arrayLength / 2);
    const peakThreshold = calculateMedian(hourlyValues.slice(0, halfLength));
    let morningPeakHours = [];

    // NajÃ­t rannÃ­ Å¡piÄku (0 do poloviny dne)
    for (let i = 0; i < halfLength; i++) {
        if (hourlyValues[i] >= peakThreshold) {
            morningPeakHours.push(i);
        }
    }

    return findContinuousBlocks(morningPeakHours, maxPeakLength, hourlyValues);
}

// Funkce pro hledÃ¡nÃ­ odpolednÃ­ Å¡piÄky
function findAfternoonPeakHours(hourlyValues, arrayLength = EXPECTED_ARRAY_LENGTH, maxPeakLength = 3) {
    if (hourlyValues.length !== arrayLength) {
        throw new Error(`Function: findAfternoonPeakHours -> Array must contain exactly ${arrayLength} elements.`);
    }

    // VÃ½poÄet mediÃ¡nu pro druhou polovinu dne
    const halfLength = Math.floor(arrayLength / 2);
    const peakThreshold = calculateMedian(hourlyValues.slice(halfLength, arrayLength));
    let afternoonPeakHours = [];

    // NajÃ­t odpolednÃ­ Å¡piÄku (polovina dne do konce)
    for (let i = halfLength; i < arrayLength; i++) {
        if (hourlyValues[i] >= peakThreshold) {
            afternoonPeakHours.push(i);
        }
    }

    return findContinuousBlocks(afternoonPeakHours, maxPeakLength, hourlyValues);
}

// Tato funkce zjiÅ¡Å¥uje souvislÃ© bloky hodin v danÃ©m poli a vracÃ­ nejdelÅ¡Ã­ blok nebo prÃ¡zdnÃ© pole
function findContinuousBlocks(hoursArray, maxPeakLength, values) {
    let blocks = [];
    let currentBlock = [];

    for (let i = 0; i < hoursArray.length; i++) {
        if (currentBlock.length === 0) {
            currentBlock.push(hoursArray[i]);
        } else if (hoursArray[i] === currentBlock[currentBlock.length - 1] + 1) {
            currentBlock.push(hoursArray[i]);
        } else {
            blocks.push(currentBlock);
            currentBlock = [hoursArray[i]];
        }
    }

    if (currentBlock.length > 0) {
        blocks.push(currentBlock);
    }

    // NajÃ­t nejdelÅ¡Ã­ blok nebo vrÃ¡tit prÃ¡zdnÃ© pole, s maximÃ¡lnÃ­ dÃ©lkou bloku
    let longestBlock = blocks.reduce((a, b) => (a.length > b.length ? a : b), []);

    if (longestBlock.length > maxPeakLength) {
        let bestBlock = longestBlock.slice(0, maxPeakLength);
        let bestAvg = calculateAverage(bestBlock, values);

        for (let i = 1; i <= longestBlock.length - maxPeakLength; i++) {
            let currentBlock = longestBlock.slice(i, i + maxPeakLength);
            let currentAvg = calculateAverage(currentBlock, values);

            if (currentAvg > bestAvg) {
                bestBlock = currentBlock;
                bestAvg = currentAvg;
            }
        }

        return bestBlock;
    } else {
        return longestBlock;
    }
}

// Tato funkce vypoÄÃ­tÃ¡ prÅ¯mÄ›rnou hodnotu pro danÃ½ blok hodin
function calculateAverage(block, values) {
    let sum = block.reduce((acc, hour) => acc + values[hour], 0);
    return sum / block.length;
}

//funkce prevede kladne cislo na zaporne a ignoruje text a zapornou hodnotu ponecha bezezmeny..
function EnsureNegativeValue(inputValue) {
    // Check if the input is a number
    if (typeof inputValue === 'number') {
        // Ensure the value is negative
        if (inputValue > 0) {
            // Convert positive value to negative
            return -Math.abs(inputValue);
        } else {
            // Leave the value unchanged if it is already negative or zero
            return inputValue;
        }
    } else {
        // If the input is not a number, return 0
        return 0;
    }
}

//funkce prevede vstupni cÃ­slo na kladnÃ© a innoruje text
function EnsurePositiveValue(inputValue) {
    // Check if the input is a number
    if (typeof inputValue === 'number') {
        // Ensure the value is positive
        if (inputValue < 0) {
            // Convert negative value to positive
            return Math.abs(inputValue);
        } else {
            // Leave the value unchanged if it is already positive or zero
            return inputValue;
        }
    } else {
        // If the input is not a number, return 0
        return 0;
    }
}

