//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutÃ­ debug zprÃ¡v
const nCountTimeStamp = 4; //kolik cyklu se mÃ¡ provest nez bude uznane platne casove razitko.
let nStatusIndicator = "";
let modbusResponse = msg.payload;

// ZÃ­skÃ¡nÃ­ poÄtu pÅ™ijatÃ½ch ÄasovÃ½ch razÃ­tek z flow contextu
let timestampCounter = flow.get("timestampCounter") || 0;

const nCode = isTimestamp(modbusResponse);

if (nCode == true) {
    // Pokud je hodnota platnÃ½m ÄasovÃ½m razÃ­tkem, zvyÅ¡te ÄÃ­taÄ
    timestampCounter += 1;

    // Pokud ÄÃ­taÄ dosÃ¡hne hodnoty nCountTimeStamp, resetuje modbusResponse a ÄÃ­taÄ
    if (timestampCounter >= nCountTimeStamp) {
        modbusResponse = 0;  // PlatnÃ© ÄasovÃ© razÃ­tko 10x za sebou
        timestampCounter = nCountTimeStamp;  // ResetovÃ¡nÃ­ ÄÃ­taÄe
    }
} else {
    // Pokud nepÅ™ijde platnÃ© ÄasovÃ© razÃ­tko, ÄÃ­taÄ se vynuluje
    timestampCounter = 0;
}

// UloÅ¾enÃ­ aktuÃ¡lnÃ­ho stavu ÄÃ­taÄe do flow contextu
flow.set("timestampCounter", timestampCounter);

if (modbusResponse != 0){
    nStatusIndicator ="on"
} else {
    nStatusIndicator ="off";
}  
const indicatorMsg = { indicator: '_tcp_connect', status: nStatusIndicator, topic: 'update-indicator' };
return { payload: indicatorMsg };


// Funkce pro zjiÅ¡tÄ›nÃ­, zda je hodnota ÄasovÃ© razÃ­tko
function isTimestamp(value) {
    if (typeof value !== 'number' || isNaN(value)) {
        return false;
    }

    const now = Date.now();
    const minTimestamp = new Date('1970-01-01').getTime(); // UNIX epoch start
    const maxTimestamp = new Date('2100-01-01').getTime(); // Future limit

    // Check if it's a millisecond timestamp
    if (value >= minTimestamp && value <= maxTimestamp) {
        return true;
    }

    // Check if it's a second timestamp (divide by 1000)
    if (value * 1000 >= minTimestamp && value * 1000 <= maxTimestamp) {
        return true;
    }

    return false;
}

