// PÅ™ijÃ­mÃ¡ bearer token z msg.payload a sestavÃ­ poÅ¾adavek
const bearerToken = msg.payload.token;
const userId = msg.payload.idUser;

// Sanitizace nÃ¡zvu instalace
name_instalation = sanitizeString(name_instalation);

let tokenName = name_instalation +`_` + generateRandomString(6);  // NÃ¡hodnÃ½ Å™etÄ›zec s dÃ©lkou 6

fSetConfigProperty("bearerToken", bearerToken); //ulozit data do globalni struktury
fSetConfigProperty("nVRM_site_id", userId); //ulozit data do globalni struktury

//global.set("bearerToken", bearerToken);

// NastavenÃ­ hlaviÄek a tÄ›la poÅ¾adavku
msg.headers = {
    "Content-Type": "application/json",
    "x-authorization": `Bearer ${bearerToken}`
};

// TÄ›lo poÅ¾adavku
msg.payload = {
    "name": tokenName
};

// NastavenÃ­ URL pro vytvoÅ™enÃ­ tokenu
msg.url = `https://vrmapi.victronenergy.com/v2/users/${userId}/accesstokens/create`;

