// GenerovÃ¡nÃ­ nÃ¡hodnÃ©ho alfanumerickÃ©ho Å™etÄ›zce
function generateRandomString(length) {
    let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
}

// Sanitizace Å™etÄ›zce - odstranÄ›nÃ­ diakritiky a nebezpeÄnÃ½ch znakÅ¯
function sanitizeString(input) {
    if (typeof input !== 'string') {
        return 'NCC-1701-D'; // VrÃ¡tit U.S.S. Enterprise NCC-1701-D, pokud vstup nenÃ­ Å™etÄ›zec
    }
    // OdstranÄ›nÃ­ diakritiky
    input = input.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    // PovolenÃ© znaky: pÃ­smena a ÄÃ­slice
    return input.replace(/[^a-zA-Z0-9]/g, '');
}

