// Funkce pro dekÃ³dovÃ¡nÃ­ base64url kÃ³dovanÃ½ch Å™etÄ›zcÅ¯
function base64urlDecode(str) {
    // Base64url na base64 konverzi
    str = str.replace(/-/g, '+').replace(/_/g, '/');

    // DekÃ³dovÃ¡nÃ­ base64
    return Buffer.from(str, 'base64').toString('utf-8');
}

// Funkce pro ovÄ›Å™enÃ­ JWT tokenu bez tajnÃ©ho klÃ­Äe (zÃ¡kladnÃ­ ovÄ›Å™enÃ­)
// Tento kÃ³d pouze zkontroluje, zda je token ve sprÃ¡vnÃ©m formÃ¡tu a zda jsou header a payload platnÃ© JSON objekty, nikoliv PODPIS!!!
function verifyJWTWithoutSecret(token) {
    // Kontrola, zda je token typu string
    if (typeof token !== 'string') {
        if (debug) {
            node.error("Token nenÃ­ platnÃ½ Å™etÄ›zec.");
        }
        return 0;  // NeplatnÃ½ formÃ¡t tokenu
    }

    // RozdÄ›lenÃ­ JWT tokenu na jeho ÄÃ¡sti
    const parts = token.split('.');

    // Kontrola, zda mÃ¡ token sprÃ¡vnÃ½ poÄet ÄÃ¡stÃ­
    if (parts.length !== 3) {
        return 0;  // NeplatnÃ½ formÃ¡t tokenu
    }

    try {
        // DekÃ³dovÃ¡nÃ­ header a payload ÄÃ¡sti tokenu
        const header = JSON.parse(base64urlDecode(parts[0]));
        const payload = JSON.parse(base64urlDecode(parts[1]));

        // Pokud je dekÃ³dovÃ¡nÃ­ ÃºspÄ›Å¡nÃ©, vrÃ¡tit pÅ¯vodnÃ­ token
        return token;
    } catch (e) {
        // Pokud doÅ¡lo k chybÄ› bÄ›hem dekÃ³dovÃ¡nÃ­, vrÃ¡tit 0
        return 0;
    }
}

