// HledÃ¡nÃ­ nÃ¡zvu senzoru podle unitID
let customName = "";
for (let key in mapping) {
    if (mapping[key].unitID === unitID) {
        customName = mapping[key].customName;
        break;
    }
}

// Pokud jsme nenaÅ¡li odpovÃ­dajÃ­cÃ­ jmÃ©no senzoru, vypÃ­Å¡eme varovÃ¡nÃ­ a ukonÄÃ­me
if (!customName) {
    node.warn(`Variable name for unitID ${unitID} not recognized!`);
    return null;
}

// NahrazenÃ­ slov "Menic" a "Teplota" slovem "Temperature"
customName = customName.replace(/Menic|Teplota/g, "Temperature");

// OvÄ›Å™enÃ­ a zpracovÃ¡nÃ­ hodnoty teploty
let rawValue = msg.payload?.[0];  // PrvnÃ­ hodnota v payload poli
if (typeof rawValue === "undefined") {
    node.warn(`Chyba: Hodnota pro unitID ${unitID} je undefined!`);
    return null;
}

let value = rawValue / 100;  // PÅ™evod na Â°C (napÅ™. 3700 â†’ 37.00Â°C)

// Oprava nÃ¡zvu pro globÃ¡lnÃ­ promÄ›nnou
let sanitizedName = customName.replace(/\s+/g, "_"); // NahrazenÃ­ mezer podtrÅ¾Ã­tky

// Debugging vÃ½pis
if (debug) {
    //node.warn(`unitID: ${unitID}, value: ${value}Â°C, customName: ${customName} -> saved as: ${sanitizedName}`);
    node.warn(`SanitizedName: ` + sanitizedName + `, Value: ` + value);
}

// UloÅ¾enÃ­ hodnoty do globÃ¡lnÃ­ promÄ›nnÃ©
global.set(sanitizedName, value);

// VÃ½stup zprÃ¡vy pro dalÅ¡Ã­ zpracovÃ¡nÃ­
msg.payload = {
    unitID: unitID,
    value: value,
    customName: customName
};

return msg;

