    }
} else {
    msg4.payload = null;
}

// VrÃ¡tÃ­me vÅ¡echny ÄtyÅ™i zprÃ¡vy, kaÅ¾dou pro jeden vÃ½stup
return [msg1, msg2, msg3, msg4];

