// Debugging flag
const debug = false;  // Nastavte na true pro zapnutÃ­ debug zprÃ¡v

//definice globalnÃ­ch funkci
const fSendUrl = global.get('fSendUrl');

if (fSendUrl) {
    const nRelayNumber = 0;
    const sIpAddress = "192.168.11.7";
    let sTurn = "off";  // VÃ½chozÃ­ hodnota
    let nTurn = 0;  // VÃ½chozÃ­ hodnota
    let n_trigger_MPTT = 500; // defaultnÃ­ hodnota triggeru
    let _url = "";

    //NaÄtenÃ­ vstupnÃ­ch dat
    const _general_stop = msg.payload._general_stop;// general stop

    // NaÄteni globÃ¡lnÃ­ch dat
    const pvPower = global.get("pvPower") || 0; // Hodnota z registru 819 

    if (_general_stop == true){
        sTurn = "off";
        nTurn = 0;
    }else{// Zkontroluj, zda pvPower je vÄ›tÅ¡Ã­ nebo rovno 1200 a nastav sTurn podle toho
        if (pvPower >= n_trigger_MPTT) {
            sTurn = "on";
