        node.warn("pvPower: " + pvPower);
        node.warn("sTurn: " + sTurn);
        node.warn("URL: " + _url);
    }
    } else {
    node.error("GlobÃ¡lnÃ­ funkce nebyly nalezeny. <- FORM CHLAZENI FVE -- MPTT");
}
return msg;

