            return null; // VrÃ¡tit null, aby zprÃ¡va nebyla odeslÃ¡na
        }
    }

} else {//zapnuty ventilator
    if (nTurn == true) {
        context.set('nCopyOnChange_url_m', _url_m);
        context.set('nWaitLoop_m', 0);
        context.set('nStopLoop_m', false);
        msg.payload = _url_m; // Odeslat zprÃ¡vu
        return msg;
    } else {//toto by nikdy nemelo nastat
        return null; // VrÃ¡tit null, aby zprÃ¡va nebyla odeslÃ¡na
        }
}

