// Debugging flag
const debug = false;  // Nastavte na true pro zapnutÃ­ debug zprÃ¡v

// Kontrola statusCode
if (msg.statusCode !== 200) {
    return null; // Pokud nenÃ­ statusCode 200, vrÃ¡tÃ­me null a ukonÄÃ­me zpracovÃ¡nÃ­
}

const response = JSON.parse(msg.payload);
let sURL = msg.responseUrl;

// RuÄnÃ­ zpracovÃ¡nÃ­ URL pro zÃ­skÃ¡nÃ­ IP adresy a hodnoty 'turn'
let ipAddress = sURL.split('/')[2];
let turnValue = sURL.includes('turn=on') ? 'on' : 'off';

let indicatorMsg = {};

// Debugging vstupnÃ­ch dat
if (debug) {
    node.warn("sURL: " + sURL);
    node.warn("ipAddress: " + ipAddress);
    node.warn("turnValue: " + turnValue);
}

if (ipAddress === '192.168.11.7') {
    indicatorMsg = { indicator: 'mptt-fan', status: turnValue, topic: 'update-indicator' };
} else if (ipAddress === '192.168.11.9') {
    indicatorMsg = { indicator: 'changer-fan', status: turnValue, topic: 'update-indicator' };
}

return { payload: indicatorMsg };
