//*****************************************************************************
//
// File Name        : 'Chlazeni MPTT trakeru'
// Title            : Cooling of Trackers in the Rack
// Author           : http://www.prochazka.zde.cz -> hacesoft 2024
// Created          : 21-10-2023, 11:00
// Revised          : 06-07-2024, 10:27
// Version          : 1.3
// Target Platform  : Node-RED
//
// This code is distributed under the GNU Public License
// Všechny informace jsou zahrnuty pod GPL licenci, pokud není explicitně uveden jiný typ licence.
// Používání těchto stránek a produktů ke komerčním účelům lze jen se souhlasem autora.
// Všechna práva vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const fSendUrl = global.get('fSendUrl');

if (fSendUrl) {
    const nRelayNumber = 0;
    const sIpAddress = "192.168.11.7";
    let sTurn = "off";  // Výchozí hodnota
    let nTurn = 0;  // Výchozí hodnota
    let n_trigger_MPTT = 1000; // defaultní hodnota triggeru
    let _url = "";

    //Načtení vstupních dat
    const _general_stop = msg.payload._general_stop;// general stop

    // Načteni globálních dat
    const pvPower = global.get("pvPower") || 0; // Hodnota z registru 819 

    if (_general_stop == true){
        sTurn = "off";
        nTurn = 0;
    }else{// Zkontroluj, zda pvPower je větší nebo rovno 1200 a nastav sTurn podle toho
        if (pvPower >= n_trigger_MPTT) {
            sTurn = "on";
            nTurn = 1;
        } else {
            sTurn = "off";
            nTurn = 0;
        }
    }

    // Vytvořit URL na základě hodnoty sTurn
    _url = fSendUrl(sIpAddress, nRelayNumber, sTurn);

    // Nastavit payload zprávy
    msg.payload = {
        _url_m: _url,
        pvPower: pvPower,
        nTurn: nTurn,
        sTurn: sTurn + " MPTT"
    };

    // Debug výpisy
    if (debug) {
        node.warn("pvPower: " + pvPower);
        node.warn("sTurn: " + sTurn);
        node.warn("URL: " + _url);
    }
    } else {
    node.error("Globální funkce nebyly nalezeny. <- FORM CHLAZENI FVE -- MPTT");
}
return msg;

