//*****************************************************************************
//
// File Name        : 'Chlazeni_CHANGERs'
// Title            : Cooling of Changers in the Rack
// Author           : http://www.prochazka.zde.cz -> hacesoft 2024
// Created          : 21-10-2023, 11:00
// Revised          : 06-07-2024, 10:27
// Version          : 1.4
// Target Platform  : Node-RED
//
// This code is distributed under the GNU Public License
// Všechny informace jsou zahrnuty pod GPL licenci, pokud není explicitně uveden jiný typ licence.
// Používání těchto stránek a produktů ke komerčním účelům lze jen se souhlasem autora.
// Všechna práva vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const fSendUrl = global.get('fSendUrl');
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');

if (fSendUrl && nConvertSignetUnsignet) {

    const nRelayNumber = 0;
    const sIpAddress = "192.168.11.9";
    let sTurn = "off";  // Výchozí hodnota
    let nTurn = 0;  // Výchozí hodnota
    let n_trigger_Changer = 700; // defaultní hodnota triggeru
    let _url = "";

    //Načtení vstupních dat
    const _general_stop = msg.payload._general_stop;// general stop

    // Definice fazi AC LOAD
    const phase1 = global.get("phase1") || 0; // Hodnota z registru 817
    const phase2 = global.get("phase2") || 0;  // Hodnota z registru 818
    const phase3 = global.get("phase3") || 0;;  // Hodnota z registru 819

    // Definice Gridu
    const grid1 = global.get("grid1") || 0; // Hodnota z registru 811
    const grid2 = global.get("grid2") || 0;  // Hodnota z registru 812
    const grid3 = global.get("grid3") || 0;;  // Hodnota z registru 813

    // Spočítat celkový odběr AC zátěže
    let totalACLoad = phase1 + phase2 + phase3;

    //Spočítat celkový odber nebo dodávku do GRIDu
    let _ABS_totalGrid = Math.abs(grid1 + grid2 + grid3); //vzdy kladna hodnota

    if (_general_stop == true){
        sTurn = "off";
        nTurn = 0;
    }else{// Zkontroluj, zda totalACLoad nebo  _ABS_totalGrid je větší jako n_trigger_Changer a podle toho nastav sTurn.
        if (totalACLoad > n_trigger_Changer || _ABS_totalGrid > n_trigger_Changer) {
            sTurn = "on";
            nTurn = 1;
        } else {
            sTurn = "off";
            nTurn = 0;
        }
    }

    // Vytvořit URL na základě hodnoty sTurn
    _url = fSendUrl(sIpAddress, nRelayNumber, sTurn);

    // Nastavit payload zprávy
    msg.payload = {
        _url_ch: _url,
        _ABS_totalGrid: _ABS_totalGrid,
        totalACLoad: totalACLoad,
        sTurn: sTurn + " CHANGER",
        nTurn: nTurn
    };

    // Debug výpisy
    if (debug) {
        node.warn("totalACLoad: " + totalACLoad);
        node.warn("sTurn: " + sTurn);
        node.warn("URL: " + _url);
        node.warn("_ABS_totalGrid: " + _ABS_totalGrid);
    }
    } else {
    node.error("Globální funkce nebyly nalezeny. <- FORM CHLAZENI FVE -- CHANGER");
}
return msg;

