//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

const nWaitLoop = 20; //kolik cyklu cekat

// Inicializace kontextových proměnných
let nCopyOnChange_url_ch = context.get('nCopyOnChange_url_ch') || "";
let nWaitLoop_ch = context.get('nWaitLoop_ch') || 0;
let nStopLoop_ch = context.get('nStopLoop_ch') || false;

// Načtení vstupních dat
const _url_ch = msg.payload && msg.payload._url_ch ? msg.payload._url_ch : "";
const nTurn = msg.payload && msg.payload.nTurn !== undefined ? msg.payload.nTurn : false;

// Zvýšení nWaitLoop_ch
nWaitLoop_ch += 1;
context.set('nWaitLoop_ch', nWaitLoop_ch);

// Debug výpisy
if (debug) {
    node.warn("_url_ch: " + _url_ch);
    node.warn("nCopyOnChange_url_ch: " + nCopyOnChange_url_ch);
    node.warn("nWaitLoop_ch: " + nWaitLoop_ch);
    node.warn("nWaitLoop: " + nWaitLoop);
    node.warn("nTurn: " + nTurn);
    node.warn("nStopLoop_ch: " + nStopLoop_ch);
}

// Porovnat aktuální hodnotu s poslední odeslanou hodnotou a nebo pri nWaitLoop a nTurn == false (20x po sobe prislo potvrzenivypnuti ventilatoru), provest vypnuti ventilatoru
if (_url_ch != nCopyOnChange_url_ch || (nWaitLoop_ch > nWaitLoop && nTurn == false)) {//vypinani ventilatoru
    if (nWaitLoop_ch > nWaitLoop && nTurn == false) {//20x posobe prislo stop, tak vypnout ventilator
        // pri nWaitLoop a nTurn == false (20x po sobe prislo potvrzenivypnuti ventilatoru), provest vypnuti ventilatoru
        if (nStopLoop_ch == false) {;
            context.set('nCopyOnChange_url_ch', _url_ch);
            context.set('nStopLoop_ch', true);
            msg.payload = _url_ch; // Odeslat zprávu
            return msg;
        } else {//toto by nikdy nemelo nastat
            return null; // Vrátit null, aby zpráva nebyla odeslána
        }
    } else {

        if (nTurn == true) {
            context.set('nCopyOnChange_url_ch', _url_ch);
            context.set('nWaitLoop_ch', 0);
            context.set('nStopLoop_ch', false);
            msg.payload = _url_ch; // Odeslat zprávu
            return msg;
        }
        else{//neprislo po sobe 20x stop fan
            return null; // Vrátit null, aby zpráva nebyla odeslána
        }
    }

} else {//zapnuty ventilator
    if (nTurn == true) {
        context.set('nCopyOnChange_url_ch', _url_ch);
        context.set('nWaitLoop_ch', 0);
        context.set('nStopLoop_ch', false);
        msg.payload = _url_ch; // Odeslat zprávu
        return msg;
    } else {//toto by nikdy nemelo nastat
        return null; // Vrátit null, aby zpráva nebyla odeslána
        }
}

