// Používání těchto stránek a produktů ke komerčním účelům lze jen se souhlasem autora.
// Všechna práva vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; //dotaz na data z globalni strukturu

if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier) {
    if("todayHourlyPrices" in msg.payload) { 
        var line1 = [];
        var line2 = [];
        var currentTimeLine = [];
        const currentTime = new Date();
        const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); // prida CZ lokaci
        const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate)); // upravi stavajici objekt cas na prijatelnou podobu pro porovnavani

        var hourlyPrices = msg.payload.todayHourlyPrices;
        var currPrice = hourlyPrices[oDate_CZ.getHours()];
        var minPrice = Math.min(...hourlyPrices);
        var maxPrice = Math.max(...hourlyPrices);

        for(var i=0; i<24;)
        {
            // spot price
            line1[i] = { "x": new Date(oDate_CZ.setUTCHours(i,0,0)), "y": hourlyPrices[i] };
            // spot treshold
            line2[i] = { "x": new Date(oDate_CZ.setUTCHours(i,0,0)), "y": spotTresholdPrice };

            if (debug){
            //   node.warn("oDate_CZ: " + oDate_CZ);
            //   node.warn("i: " + i);
            //   node.warn("hourlyPrices[i] : " + hourlyPrices[i] );
            }
            i++;
        }   
        // add one more point to finish chart line
        line1[24] = { "x": new Date(oDate_CZ.setUTCHours(23,59,59)), "y": hourlyPrices[23] };
        line2[24] = { "x": new Date(oDate_CZ.setUTCHours(23,59,59)), "y": spotTresholdPrice };


        // Získání časového posunu mezi lokálním časem a UTC v minutách
        // Výpočet časového posunu v hodinách: Použitím metody getTimezoneOffset na aktuálním datu currentTime zjistíme časový posun mezi 
        // lokálním časem a UTC. Tento posun je v minutách, takže jej převedeme na hodiny a násobíme -1, abychom získali obrácený posun.
        // Úprava aktuálního času: K aktuálnímu času currentTime přičítáme(nebo odečítáme) časový posun v 
        // milisekundách(timezoneOffsetHours * 60 * 60 * 1000), abychom získali čas v UTC.
        var timezoneOffsetHours = -currentTime.getTimezoneOffset() / 60;
        var adjustedCurrentTime = new Date(currentTime.getTime() + timezoneOffsetHours * 60 * 60 * 1000);

        if (debug) {
            node.warn("adjustedCurrentTime: " + adjustedCurrentTime);
            node.warn("sDate: " + sDate);
            node.warn("currPrice: " + currPrice);
            node.warn("Časový posun (hodin): " + timezoneOffsetHours);
        }

        // current time line marker
        // Řádky currentTimeLine[0], currentTimeLine[1] a currentTimeLine[2] slouží k vytvoření čáry reprezentující aktuální čas na grafu. 
        // Tyto tři body určují pozici a výšku čáry:

        // X: Aktuální čas(currentDate).
        // Y: Aktuální cena(currPrice).
        // Tímto bodem začíná čára na grafu na úrovni aktuální ceny.
        currentTimeLine[0] = { "x": adjustedCurrentTime, "y": currPrice };
        // X: Aktuální čas (currentDate).
        // Y: Nejnižší cena dne nebo spotová prahová cena minus 0, 1.
        // Tento bod určuje spodní část čáry, která se nachází těsně pod minimální cenou dne nebo pod spotovou prahovou cenou.
        currentTimeLine[1] = { "x": adjustedCurrentTime, "y": ((minPrice < spotTresholdPrice) ? minPrice : spotTresholdPrice) - 0.1 };
        // X: Aktuální čas (currentDate).
        // Y: Nejvyšší cena dne nebo spotová prahová cena plus 0, 1.
        // Tento bod určuje horní část čáry, která se nachází těsně nad maximální cenou dne nebo nad spotovou prahovou cenou.
        currentTimeLine[2] = { "x": adjustedCurrentTime, "y": ((maxPrice > spotTresholdPrice) ? maxPrice : spotTresholdPrice) + 0.1 };


        msg.payload = [{
            "series": ["Kč/kWh", "Spot limit", "Aktuálně"],
            "data": [line1, line2, currentTimeLine]
        }];

        msg.minmax = minPrice.toFixed(2) + " | " + maxPrice.toFixed(2);
    }
    else{// clear chart data to reset, data not available
        msg.payload = [];
    }
} else {// nenalezeny globalni funkce
    node.error("Globální funkce nebyly nalezeny. <-- FORM SPOT GRAF -- Prepare today chart data");
}

return msg;

// reset chart data
msg.minmax = "---";
msg.payload = [];
return msg;

// reset data
msg.fcolor = "grey";
msg.enabled = flow.get("isSpotAutoCtrlEnabled");
msg.payload = {"currentPrice": "---"};

return msg;

