// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

// Function to handle errors
var errorMessage = msg.error;

//definice globalních funkci
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

const isSpotAutoCtrlEnabled = !!fGetConfigProperty("isSpotAutoCtrlEnabled"); //dotaz na data z globalni strukturu
const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0; //dotaz na data z globalni strukturu

let nSpot = 0;
let swOnOff = 0;

if (typeof msg.payload === 'object' && "currentPrice" in msg.payload) {

    const currentPrice = msg.payload.currentPrice;
    const todayHourlyPrices = msg.payload.todayHourlyPrices;
    // Ulozeni dat do globalnich promennych, ktere jsou dostupne mezi flow
    global.set("currentPrice", currentPrice);
    global.set("todayHourlyPrices", todayHourlyPrices);

    if (isSpotAutoCtrlEnabled) {
        if (currentPrice < spotTresholdPrice) {
            nSpot = 0;
            swOnOff = 0;
        } else if (currentPrice >= spotTresholdPrice) {
            nSpot = 1;
            swOnOff = 1;
        }
        // Nastavit vystup
        msg.payload = {
            "nSpot": nSpot,
            "currentPrice": currentPrice,
            "hCurrentPrice": 1,
            "swOnOff": swOnOff,
            "isSpotAutoCtrlEnabled": isSpotAutoCtrlEnabled,
            "spotTresholdPrice": spotTresholdPrice
        };
        if (debug) {
            node.warn("currentPrice: " + currentPrice);
            node.warn("spotTresholdPrice: " + spotTresholdPrice);
            node.warn("nSpot: " + nSpot);
        }
    } else { //obsluha chyby
        msg.payload = hErrorHandle();
    }
} else { //obsluha chyby
    msg.payload = hErrorHandle();
}
return msg;

function hErrorHandle() {
    // You can customize your error handling logic here
    var errorPayload = {
        "hCurrentPrice": 0,
    };

    // Log the error if debugging is enabled
    if (debug && errorMessage) {
        node.warn("Error caught: " + errorMessage.toString());
    }

    return errorPayload;
}

