//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

// Definice globalních funkcí
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');

if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier) {
    let series1 = Array(24).fill(0); // Initialize with zeros for 24 hours
    let series2 = Array(24).fill(0); // Initialize with zeros for 24 hours
    let series3 = Array(24).fill(0); // Initialize with zeros for 24 hours
    let labels = [];

    const date = new Date();
    const sDate = date.toLocaleString(sCounty_Code, { timeZone: sTZidentifier });
    const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate));

    // Načtení dat do globálních proměnných, které jsou dostupné mezi flow
    const nBuffer = global.get("sAPI_live_feed") || {};

    // Kontrola, zda nBuffer.records existuje
    if (nBuffer.records && Array.isArray(nBuffer.records.Pdc) && Array.isArray(nBuffer.records.total_consumption) && Array.isArray(nBuffer.records.total_solar_yield)) {

        // Popisky pro 24 hodin od 00:00 do 23:00
        labels = Array.from({ length: 24 }, (_, i) => `${i}:00`);

        // Pomocná funkce pro získání hodiny z timestampu
        const getHour = (timestamp) => new Date(timestamp).getHours();

        // Naplňte data podle hodin
        nBuffer.records.Pdc.forEach(item => {
            if (item && item[0] && item[1]) {
                const hour = getHour(item[0]);
                series1[hour] = parseFloat(item[1] / 1000).toFixed(1); // Pdc přepočet na kW
            }
        });

        nBuffer.records.total_consumption.forEach(item => {
            if (item && item[0] && item[1]) {
                const hour = getHour(item[0]);
                series2[hour] = parseFloat(item[1]).toFixed(1); // total_consumption v kWh
            }
        });

        nBuffer.records.total_solar_yield.forEach(item => {
            if (item && item[0] && item[1]) {
                const hour = getHour(item[0]);
                series3[hour] = parseFloat(item[1]).toFixed(1); // total_solar_yield v kWh
            }
        });

        // Vytvoření výstupního formátu
        msg.payload = [{
            "series": ["Pdc", "Spotřeba", "Solární energie"],
            "data": [series1, series2, series3],
            "labels": labels,
            "colors": ["#FF0000", "#00ff00", "#0000FF"],
            "lines": [false, false, true] // 'FV výroba' as a line
        }];

    } else {
        if (debug) {
            node.warn("nBuffer.records nebo jeho některé části neexistují nebo nejsou pole.");
        }
    }

    // Debugging
    if (debug) {
        node.warn("series1: " + JSON.stringify(series1));
        node.warn("series2: " + JSON.stringify(series2));
        node.warn("series3: " + JSON.stringify(series3));
        node.warn("labels: " + JSON.stringify(labels));
    }
} else {
    // nenalezeny globalni funkce
    node.error("Globální funkce nebyly nalezeny. <-- FORM GUI -- Parse_DATA_graf");
}

return msg;

