global.set('getConfigProperty', function (propertyPath) {
    let config = global.get("config") || {};
    return propertyPath.split('.').reduce((o, p) => (o ? o[p] : undefined), config);
});

//*****************************************************************************
//ulozi data do globalniho obektu
//priklad: setConfigProperty("connectorConfig.tcpHost", "192.168.50.250");
global.set('setConfigProperty',function (propertyPath, value) {
    let config = global.get("config") || {};
    let schema = config;  // a moving reference to internal objects within config
    let pList = propertyPath.split('.');
    let len = pList.length;
    for(let i = 0; i < len-1; i++) {
        let elem = pList[i];
        if( !schema[elem] ) schema[elem] = {};
        schema = schema[elem];
    }

    schema[pList[len-1]] = value;
    global.set("config", config);
});

//*****************************************************************************
global.set('removeSpacesAroundDots', function (inputString) {
    return inputString.replace(/ \. /g, '.').replace(/\. /g, '.').replace(/ \./g, '.');
});

//*****************************************************************************
global.set('sExtractTime', function (dateString) {
    const hFixedDate = fSetFixedDate();
    let parts = dateString.split(' ');
    //let dateParts = parts[0].split('.');
    let timeParts = parts[1].split(':');

      let date = new Date(
          hFixedDate.getFullYear(), // rok
          hFixedDate.getMonth(), // měsíc (od 0 do 11)
          hFixedDate.getDate(), // den
          parseInt(timeParts[0], 10), // hodina
          parseInt(timeParts[1], 10), // minuta
          parseInt(timeParts[2], 10) // sekunda
    );

    return date;
});

//*****************************************************************************
global.set('mExtractTime',function mExtractTime(milliseconds) {
        const hFixedDate = fSetFixedDate();
        let date = new Date(milliseconds);

    let oDate = new Date(
        hFixedDate.getFullYear(), // rok
        hFixedDate.getMonth(), // měsíc (od 0 do 11)
        hFixedDate.getDate(), // den
        date.getUTCHours(), // hodina
        date.getUTCMinutes(), // minuta
        date.getUTCSeconds(), // sekunda
        0 // milisekunda
    );
    return oDate;
});

//*****************************************************************************
// vrati casove razitko dnesniho dne 00:00 v sekundach
global.set('getStartOfDayTimestamp', function getStartOfDayTimestamp() {
    // Získání pevného data pro dnešní den v 00:00 pomocí fSetFixedDate
    let fixedDate = fSetFixedDate();
    fixedDate.setHours(0, 0, 0, 0);
    return Math.floor(fixedDate.getTime() / 1000); // Vrátí časové razítko v sekundách
});

//*****************************************************************************
// vrati casove razitko dnesniho dne 23:59:0:0 v sekundach
global.set('getEndOfDayTimestamp', function getEndOfDayTimestamp() {
    // Získání pevného data pro dnešní den v 00:00 pomocí fSetFixedDate
    let fixedDate = fSetFixedDate();
    // Nastavení času na 23:59:0
    fixedDate.setHours(23, 59, 0, 0);
    // Vrácení časového razítka v sekundách
    return Math.floor(fixedDate.getTime() / 1000);
});

//*****************************************************************************
function fSetFixedDate(){
    const hFIXDate = new Date();
    let hFixedDate = new Date(
        hFIXDate.getUTCFullYear(), // rok
        hFIXDate.getUTCMonth(), // měsíc (od 0 do 11)
        hFIXDate.getUTCDate(), // den
        0, // hodina
        0, // minuta
        0, // sekunda
        0 // milisekunda
    );
    return hFixedDate;
}

//*****************************************************************************
function fSetFixedDateA() {
    const hFIXDate = new Date();
    let hFixedDate = new Date(
        hFIXDate.getFullYear(), // rok (lokální)
        hFIXDate.getMonth(), // měsíc (lokální)
        hFIXDate.getDate(), // den (lokální)
        0, // hodina
        0, // minuta
        0, // sekunda
        0 // milisekunda
    );
    return hFixedDate;
}
//*****************************************************************************
// Funkce pro generování URL
global.set('fSendUrl',function fSendUrl(sIpAddress, nRelayNumber, sTurn) {
    return `http://${sIpAddress}/relay/${nRelayNumber}?turn=${sTurn}`;
});

//*****************************************************************************
// Convert Function Number Signet To Unsignet
global.set('nConvertSignetUnsignet',function nConvertSignetUnsignet(nNumber) {
    if (nNumber > 32767) nNumber -= 65536; 
    return nNumber;
});

//*****************************************************************************
// Convert Function Number Unsignet To Signet
global.set('nConvertUnsignetToSignet', function nConvertUnsignetToSignet(nNumber) {
    if (nNumber < 0) nNumber += 65536;
    return nNumber;
});

