const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const mExtractTime = global.get('mExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');


if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier && mExtractTime && fGetConfigProperty) {
    const tStopBattery = fGetConfigProperty("tStopBattery") || 0;
    const tStartBattery = fGetConfigProperty("tStartBattery") || 0;

    // Získejte aktuální čas ve správném časovém pásmu
    var currentTime = new Date();
    var sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); 

    // Převod aktuálního času na Date objekt
    var oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate));

    // Převod časů začátku a konce na Date objekty
    var tStartBatteryTime = mExtractTime(tStartBattery);
    var tStopBatteryTime = mExtractTime(tStopBattery);

    if (tStartBatteryTime && tStopBatteryTime) {
        if (debug) {
            node.warn("tStartBattery: " + tStartBatteryTime);
            node.warn("tStopBattery: " + tStopBatteryTime);
            node.warn("oDate_CZ: " + oDate_CZ);
        }

        msg.payload = `Aktuální čas je: (${tStartBatteryTime.toLocaleTimeString()} - ${tStopBatteryTime.toLocaleTimeString()}).`;
    } else {
        msg.payload = 'Čas začátku nebo konce není nastaven.';
    }
    } else {
    node.error("Globální funkce nebyly nalezeny. <- FORM GUI -- Control Set Time");
}
return msg;

