// Function to decode base64url encoded strings
function base64urlDecode(str) {
    // Base64url to base64 conversion
    str = str.replace(/-/g, '+').replace(/_/g, '/');

    // Decode base64
    return Buffer.from(str, 'base64').toString('utf-8');
}

// Function to verify a JWT token without a secret key (basic validation)
//Tento kód pouze zkontroluje, zda je token ve správném formátu a zda jsou header a payload platné JSON objekty, nikoliv PODPIS !!!
function verifyJWTWithoutSecret(token) {
    // Split the JWT token into its parts
    const parts = token.split('.');

    // Check if the token has the correct number of parts
    if (parts.length !== 3) {
        return 0;  // Invalid token format
    }

    try {
        // Decode the header and payload parts of the token
        const header = JSON.parse(base64urlDecode(parts[0]));
        const payload = JSON.parse(base64urlDecode(parts[1]));

        // If decoding is successful, return the original token
        return token;
    } catch (e) {
        // If there was an error during decoding, return 0
        return 0;
    }
}

