// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');
const mExtractTime = global.get('mExtractTime');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');

try {
    if (removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier && nConvertSignetUnsignet && mExtractTime) {
        const nRezerva = 230;
        let nSet_Grid_Point = 0;

        // Definice fazi AC LOAD
        const phase1 = parseFloat(msg.payload.phase1) || 0;  // Hodnota z registru 817
        const phase2 = parseFloat(msg.payload.phase2) || 0;  // Hodnota z registru 818
        const phase3 = parseFloat(msg.payload.phase3) || 0;  // Hodnota z registru 819

        // Definice Gridu
        const grid1 = nConvertSignetUnsignet(parseFloat(msg.payload.grid1)) || 0;  // Hodnota z registru 811
        const grid2 = nConvertSignetUnsignet(parseFloat(msg.payload.grid2)) || 0;  // Hodnota z registru 812
        const grid3 = nConvertSignetUnsignet(parseFloat(msg.payload.grid3)) || 0;  // Hodnota z registru 813

        // Definice vyroby FV panelu
        const pvPower = parseFloat(msg.payload.totalPVPower) || 0;  // Hodnota z registru 819

        // Definice stavu nabiti baterie
        const nBattery_State_Charge = parseFloat(msg.payload.nBattery_State_Charge) || 0;  // Hodnota z registru 843
        const nBattery_Power = parseFloat(msg.payload.nBattery_Power) || 0; // Battery Power (System) 842 ve W
        const nBattery_Current = parseFloat(msg.payload.nBattery_Current) || 0; // Battery Power (System) 841 ve W
        const nBattery_Voltage = parseFloat(msg.payload.nBattery_Voltage) || 0; // Battery Power (System) 840 ve W

        // Pole dnesniho spotu
        const todayHourlyPrices = global.get("todayHourlyPrices");

        // Ulozeni dat do globalnich promennych, ktere jsou dostupne mezi flow
        global.set("phase1", phase1);
        global.set("phase2", phase2);
        global.set("phase3", phase3);
        global.set("pvPower", pvPower);
        global.set("nBattery_State_Charge", nBattery_State_Charge);
        global.set("grid1", grid1);
        global.set("grid2", grid2);
        global.set("grid3", grid3);
        global.set("nBattery_Power", nBattery_Power);
        global.set("nBattery_Current", nBattery_Current);
        global.set("nBattery_Voltage", nBattery_Voltage);

        // Načteni globalnich dat
        const nGridConsumptionEnable = global.get("nGridConsumptionEnable") || 0; // true znamena zapnut přepinač NUCENY ODBER Z GRIDu
        const nSetGridValue = global.get("nSetGridValue") || 0; // Hodnota sítoveho odberu
        const isSpotAutoCtrlEnabled = global.get("isSpotAutoCtrlEnabled") || 0; // true znamená Automaticky podle SPOTu
        const spotTresholdPrice = global.get("spotTresholdPrice") || 0; // Limitni cena
        const currentPrice = global.get("currentPrice") || 0; // Aktualni SPOT cena
        const nPretoky = global.get("nPretoky") || 0; // Pretoky povoleny- true, zakazany - false
        const switchDelayCharging = global.get("switchDelayCharging") || 0; // true znamena ze je aktivovana funkce posunuti nabijeni baterie
        const tDefaultTimeStart = global.get("tDefaultTimeStart") || 0; // Defaultni cas zacatku posunuti nabijeni beterie
        const tStartBattery = global.get("tStartBattery") || 0; // Zadany cas start posunu nabijeni beterie
        const tStopBattery = global.get("tStopBattery") || 0; // Zadany cas stop posunu nabijeni beterie

        const nMorningPeakBatterySales = global.get("nMorningPeakBatterySales") || 0; // Switch Prodej baterie v ranní špičce
        const nEveningPeakBatterySales = global.get("nEveningPeakBatterySales") || 0; // Switch Prodej baterie ve večerní špičce
        const nMorningSOC_sales = global.get("nMorningSOC_sales") || 0; // Procenta - Prodej Rano do SOC
        const nEveningSOC_sales = global.get("nEveningSOC_sales") || 0; // Procenta - Prodej Večer do SOC

        const currentTime = new Date(); // aktualni cas
        const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier }); // prida CZ lokaci

        const oDate_CZ = sExtractTime(removeSpacesAroundDots(sDate)); // upravi stavajici obekt cas na prijatelnou podobu pro porovnavani

        const tStartBatteryTime = mExtractTime(tStartBattery); // vstupni promenna je jen milisekundy a nejde porovnavat s aktualnim casem, tak opet uprava na prijatelnou podobu pro porovnavani.
        const tStopBatteryTime = mExtractTime(tStopBattery);

        // Spočítat celkový odběr AC zátěže
        let totalACLoad = phase1 + phase2 + phase3;

        // Hleda ranni a odpoleni peaky
        // prvni promenna je pole aktualniho spot a druha promenna rika jak dlouhy peak muse bejt
        const morningPeak1 = findMorningPeakHours(todayHourlyPrices, 2);
        const afternoonPeak1 = findAfternoonPeakHours(todayHourlyPrices, 2);

        // Debugging
        if (debug) {
            //node.warn("global.get(todayHourlyPrices): " + todayHourlyPrices);
            //node.warn("global.get(morningPeak1): " + morningPeak1);
            //node.warn("global.get(afternoonPeak1): " + afternoonPeak1);
            node.warn("tStartBatteryTime: " + tStartBatteryTime);
            node.warn("tStopBatteryTime: " + tStopBatteryTime);
            node.warn("oDate_CZ: " + oDate_CZ);
            node.warn("nSet_Grid_Point: " + nSet_Grid_Point);
        }

        nSet_Grid_Point = fSet_Grid(nGridConsumptionEnable, nSetGridValue, isSpotAutoCtrlEnabled, spotTresholdPrice, currentPrice, nPretoky);    

        let nZbytekFV = pvPower - (totalACLoad + nRezerva);

        // funkce na posun nabijeni baterii
        if (tStartBatteryTime && tStopBatteryTime) {
            if (switchDelayCharging == true) { // true znamena ze je aktivovana funkce posunuti nabijeni baterie
                if (nPretoky == true) { // povolene pretoky
                    if (oDate_CZ >= tStartBatteryTime && oDate_CZ <= tStopBatteryTime) { // Aktuální čas je v rozsahu
                        if (nZbytekFV > 0) { // Prebytek FV je kladna hodnota
                            nSet_Grid_Point = -nZbytekFV; // prevest na zapornou hodnotu
                        } else { // mala vyroba z FV panelu
                            nSet_Grid_Point = 0;
                            //node.warn("1: ");
                        }
                    } else { // Aktuální čas je mimo rozsah
                        // nSet_Grid_Point = fSet_Grid(nGridConsumptionEnable, nSetGridValue, isSpotAutoCtrlEnabled, spotTresholdPrice, currentPrice, nPretoky);  
                        //node.warn("2: ");  
                    }
                } else { // Čas začátku nebo konce není nastaven
                    nSet_Grid_Point = 0;
                    //node.warn("3: ");
                }
            }
        }

        // funkce vybití baterie při ranní špičce
        if (nMorningPeakBatterySales) {
            if (morningPeak1.length > 0) {
                const peakTimeStart = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), morningPeak1[0], 0, 0, 0);
                const peakTimeEnd = new Date(oDate_CZ.getFullYear(), oDate_CZ.getMonth(), oDate_CZ.getDate(), morningPeak1[morningPeak1.length - 1], 59, 0, 0);

                if (debug) {
                    //node.warn("-----------------------------------------");
                    //node.warn("global.get(morningPeak1): " + morningPeak1);
                    //node.warn("peakTimeStart: " + peakTimeStart);
                    //node.warn("peakTimeEnd: " + peakTimeEnd);
                    //node.warn("oDate_CZ: " + oDate_CZ);
                    //node.warn("global.get(nBattery_State_Charge): " + nBattery_State_Charge);
                    //node.warn("nSet_Grid_Point: " + nSet_Grid_Point);
                }

                if (oDate_CZ >= peakTimeStart && oDate_CZ < peakTimeEnd) {
                    if (nBattery_State_Charge >= nMorningSOC_sales) {
                        nSet_Grid_Point = -5000;
                    }
                }
            }
        }

        // Nastavit celkový odběr jako novou hodnotu pro nastavení sítě
        msg.payload = { "nSet_Grid_Point": nSet_Grid_Point,
                        "pvPower": pvPower,
                        "totalACLoad": totalACLoad,
                        "nZbytekFV": nZbytekFV
                        };

        } else {// nenalezeny globalni funkce
        node.error("Globální funkce nebyly nalezeny. <-- FORM BATTERY CONTROL-- AC LOAD");
    }
    return msg;
} catch (error) {
    node.error("Error in script: " + error.message);
}

// funkce nastavuje GridPoint podle zadane hodnoty
function fSet_Grid(nGridConsumptionEnable, nSetGridValue, isSpotAutoCtrlEnabled, spotTresholdPrice, currentPrice, nPretoky) {
    let nSetGridPoint = 0;

    if (currentPrice > 0) { // Dokud neni zaporný spot
        if (nGridConsumptionEnable == true) { // zapnuy nuceny odber z Gridu
            // if(spotTresholdPrice <= currentPrice) // aktualni cena je rovne nebo vetsi nastavena limitni cena
            nSetGridPoint = nSetGridValue;
        }
    }
    return nSetGridPoint; 
}

// Tato funkce vypočítá medián zadaných hodnot
function calculateMedian(values) {
    values = values.slice().sort((a, b) => a - b);
    const mid = Math.floor(values.length / 2);

    if (values.length % 2 === 0) {
        return (values[mid - 1] + values[mid]) / 2;
    } else {
        return values[mid];
    }
}

// Tyto funkce používají calculateMedian k určení prahové hodnoty pro ranní a odpolední hodiny
function findMorningPeakHours(hourlyValues, maxPeakLength = 3) {
    if (hourlyValues.length !== 24) {
        throw new Error('Array must contain exactly 24 elements.');
    }

    const peakThreshold = calculateMedian(hourlyValues.slice(0, 12));
    let morningPeakHours = [];

    // Najít ranní špičku (0-11 hodin)
    for (let i = 0; i < 12; i++) {
        if (hourlyValues[i] >= peakThreshold) {
            morningPeakHours.push(i);
        }
    }

    return findContinuousBlocks(morningPeakHours, maxPeakLength, hourlyValues);
}

function findAfternoonPeakHours(hourlyValues, maxPeakLength = 3) {
    if (hourlyValues.length !== 24) {
        throw new Error('Array must contain exactly 24 elements.');
    }

    const peakThreshold = calculateMedian(hourlyValues.slice(12, 24));
    let afternoonPeakHours = [];

    // Najít večerní špičku (12-23 hodin)
    for (let i = 12; i < 24; i++) {
        if (hourlyValues[i] >= peakThreshold) {
            afternoonPeakHours.push(i);
        }
    }

    return findContinuousBlocks(afternoonPeakHours, maxPeakLength, hourlyValues);
}

// Tato funkce zjišťuje souvislé bloky hodin v daném poli a vrací nejdelší blok nebo prázdné pole
function findContinuousBlocks(hoursArray, maxPeakLength, values) {
    let blocks = [];
    let currentBlock = [];

    for (let i = 0; i < hoursArray.length; i++) {
        if (currentBlock.length === 0) {
            currentBlock.push(hoursArray[i]);
        } else if (hoursArray[i] === currentBlock[currentBlock.length - 1] + 1) {
            currentBlock.push(hoursArray[i]);
        } else {
            blocks.push(currentBlock);
            currentBlock = [hoursArray[i]];
        }
    }

    if (currentBlock.length > 0) {
        blocks.push(currentBlock);
    }

    // Najít nejdelší blok nebo vrátit prázdné pole, s maximální délkou bloku
    let longestBlock = blocks.reduce((a, b) => (a.length > b.length ? a : b), []);

    if (longestBlock.length > maxPeakLength) {
        let bestBlock = longestBlock.slice(0, maxPeakLength);
        let bestAvg = calculateAverage(bestBlock, values);

        for (let i = 1; i <= longestBlock.length - maxPeakLength; i++) {
            let currentBlock = longestBlock.slice(i, i + maxPeakLength);
            let currentAvg = calculateAverage(currentBlock, values);

            if (currentAvg > bestAvg) {
                bestBlock = currentBlock;
                bestAvg = currentAvg;
            }
        }

        return bestBlock;
    } else {
        return longestBlock;
    }
}

// Tato funkce vypočítá průměrnou hodnotu pro daný blok hodin
function calculateAverage(block, values) {
    let sum = block.reduce((acc, hour) => acc + values[hour], 0);
    return sum / block.length;
}

