});

global.set('sExtractTime', function (dateString) {
    let parts = dateString.split(' ');
    let dateParts = parts[0].split('.');
    let timeParts = parts[1].split(':');

    let date = new Date(
        parseInt(dateParts[2], 10), // rok
        parseInt(dateParts[1], 10) - 1, // měsíc (od 0 do 11)
        parseInt(dateParts[0], 10), // den
        parseInt(timeParts[0], 10), // hodina
        parseInt(timeParts[1], 10), // minuta
        parseInt(timeParts[2], 10) // sekunda
    );

    return date;
});

