            msg.highlight = 'yellow';
            msg.timeout = "5";
            msg.payload = "Načten POUZE TOKEN";
        }
    } else {
        // Nahrat celou konfiguraci
        global.set('config', config);
        // Nastavit zprávu v případě úspěchu
        msg.highlight = 'green';
        msg.timeout = "5";
        msg.payload = "Uspěšně načtena konfigurace";
    }

    return msg;
} catch (e) {
    // Pokud dojde k chybě při parsování JSON
    // Debugging
    if (debug) {
        node.error('Chyba při parsování JSON: ' + e.message);
    }

    // Nastavit zprávu v případě chyby
    msg.highlight = 'red';
    msg.timeout = "5";
    msg.payload = "Nepodařilo se načíst konfiguraci: " + e.message;

    return msg;
}

