// Všechny informace jsou zahrnuty pod GPL licenci, pokud není explicitně uveden jiný typ licence.
// Používání těchto stránek a produktů ke komerčním účelům lze jen se souhlasem autora.
// Všechna práva vyhrazena (c) 1997 - 2024 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv
const nCountTimeStamp = 4; //kolik cyklu se má provest nez bude uznane platne casove razitko.
let nStatusIndicator = "";
let modbusResponse = msg.payload;

// Získání počtu přijatých časových razítek z flow contextu
let timestampCounter = flow.get("timestampCounter") || 0;

const nCode = isTimestamp(modbusResponse);

if (nCode == true) {
    // Pokud je hodnota platným časovým razítkem, zvyšte čítač
    timestampCounter += 1;

    // Pokud čítač dosáhne hodnoty nCountTimeStamp, resetuje modbusResponse a čítač
    if (timestampCounter >= nCountTimeStamp) {
        modbusResponse = 0;  // Platné časové razítko 10x za sebou
        timestampCounter = nCountTimeStamp;  // Resetování čítače
    }
} else {
    // Pokud nepřijde platné časové razítko, čítač se vynuluje
    timestampCounter = 0;
}

// Uložení aktuálního stavu čítače do flow contextu
flow.set("timestampCounter", timestampCounter);

if (modbusResponse != 0){
    nStatusIndicator ="on"
} else {
    nStatusIndicator ="off";
}  
const indicatorMsg = { indicator: '_tcp_connect', status: nStatusIndicator, topic: 'update-indicator' };
return { payload: indicatorMsg };


// Funkce pro zjištění, zda je hodnota časové razítko
function isTimestamp(value) {
    if (typeof value !== 'number' || isNaN(value)) {
        return false;
    }

    const now = Date.now();
    const minTimestamp = new Date('1970-01-01').getTime(); // UNIX epoch start
    const maxTimestamp = new Date('2100-01-01').getTime(); // Future limit

    // Check if it's a millisecond timestamp
    if (value >= minTimestamp && value <= maxTimestamp) {
        return true;
    }

    // Check if it's a second timestamp (divide by 1000)
    if (value * 1000 >= minTimestamp && value * 1000 <= maxTimestamp) {
        return true;
    }

    return false;
}

