//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

let name_instalation = fGetConfigProperty("name_instalation"); //dotaz na data z globalni strukturu

// Přijímá bearer token z msg.payload a sestaví požadavek
const bearerToken = msg.payload.token;
const userId = msg.payload.idUser;

// Sanitizace názvu instalace
name_instalation = sanitizeString(name_instalation);

let tokenName = name_instalation +`_` + generateRandomString(6);  // Náhodný řetězec s délkou 6

fSetConfigProperty("bearerToken", bearerToken); //ulozit data do globalni struktury
fSetConfigProperty("nVRM_site_id", userId); //ulozit data do globalni struktury

//global.set("bearerToken", bearerToken);

// Nastavení hlaviček a těla požadavku
msg.headers = {
    "Content-Type": "application/json",
    "x-authorization": `Bearer ${bearerToken}`
};

// Tělo požadavku
msg.payload = {
    "name": tokenName
};

// Nastavení URL pro vytvoření tokenu
msg.url = `https://vrmapi.victronenergy.com/v2/users/${userId}/accesstokens/create`;

return msg;

// Generování náhodného alfanumerického řetězce
function generateRandomString(length) {
    let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
}

// Sanitizace řetězce - odstranění diakritiky a nebezpečných znaků
function sanitizeString(input) {
    if (typeof input !== 'string') {
        return 'NCC-1701-D'; // Vrátit U.S.S. Enterprise NCC-1701-D, pokud vstup není řetězec
    }
    // Odstranění diakritiky
    input = input.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    // Povolené znaky: písmena a číslice
    return input.replace(/[^a-zA-Z0-9]/g, '');
}

