// Přidejte kontrolu, zda je `sBuffer` typu string
if (typeof sBuffer !== 'string') {
    if (debug) {
        node.error("Vstup není platný JWT token (není řetězec).");
    }
    return null;
}

const result = verifyJWTWithoutSecret(sBuffer); // kontrola na platný token

fSetConfigProperty("bearerToken", result); // uložit data do globální struktury


// Debugging
if (debug) {
    node.warn("bearerToken: Vstup z inputu: " + sBuffer);
    node.warn("bearerToken: Výstup z kontroly platnosti tokenu: " + result);
}

return null;

// Funkce pro dekódování base64url kódovaných řetězců
function base64urlDecode(str) {
    // Base64url na base64 konverzi
    str = str.replace(/-/g, '+').replace(/_/g, '/');

    // Dekódování base64
    return Buffer.from(str, 'base64').toString('utf-8');
}

// Funkce pro ověření JWT tokenu bez tajného klíče (základní ověření)
// Tento kód pouze zkontroluje, zda je token ve správném formátu a zda jsou header a payload platné JSON objekty, nikoliv PODPIS!!!
function verifyJWTWithoutSecret(token) {
    // Kontrola, zda je token typu string
    if (typeof token !== 'string') {
        if (debug) {
            node.error("Token není platný řetězec.");
        }
        return 0;  // Neplatný formát tokenu
    }

    // Rozdělení JWT tokenu na jeho části
    const parts = token.split('.');

    // Kontrola, zda má token správný počet částí
    if (parts.length !== 3) {
        return 0;  // Neplatný formát tokenu
    }

    try {
        // Dekódování header a payload části tokenu
        const header = JSON.parse(base64urlDecode(parts[0]));
        const payload = JSON.parse(base64urlDecode(parts[1]));

        // Pokud je dekódování úspěšné, vrátit původní token
        return token;
    } catch (e) {
        // Pokud došlo k chybě během dekódování, vrátit 0
        return 0;
    }
}

