const debug = false;  // Debugging flag

// Base64 obrázek pro vybíjení
const imageBase64Vybijeni = "data:image/png;base64,";

// Base64 obrázek pro nabíjení
const imageBase64Nabijeni = "data:image/png;base64,";

// Globální funkce
const AutoformatValue = global.get('AutoformatValue');
const nConvertSignetUnsignet = global.get('nConvertSignetUnsignet');

// Načtení aktuálních hodnot
let nBuffer = 0;
let nBattery_input_Wh = global.get("nBattery_input_Wh") || 0;
let nBattery_output_Wh = global.get("nBattery_output_Wh") || 0;
let nBatteryALL_input_Wh = global.get("nBatteryALL_input_Wh") || 0;
let nBatteryALL_output_Wh = global.get("nBatteryALL_output_Wh") || 0;
let nBattery_Power = global.get("nBattery_Power") || 0;   // Battery Power (System) 842 ve W

let nSignet_Battery_Power = nConvertSignetUnsignet(nBattery_Power);

if (nSignet_Battery_Power > 0) {            //nabíjení beterie
    nBuffer = nSignet_Battery_Power / 3600; //vypočet
    nBattery_input_Wh += nBuffer;           //denní statistika
    nBatteryALL_input_Wh += nBuffer;        //celková, dokud se nevymaže
} else {                                    //vybíjení baterie
    nBuffer = Math.abs(nSignet_Battery_Power) / 3600;   //výpočet
    nBattery_output_Wh += nBuffer;          //denní statistika
    nBatteryALL_output_Wh += nBuffer;       //celková, dokud se nevymaže
}


// Uložení do globálních proměnných
global.set("nBattery_input_Wh", nBattery_input_Wh);
global.set("nBattery_output_Wh", nBattery_output_Wh);
global.set("nBatteryALL_input_Wh", nBatteryALL_input_Wh);
global.set("nBatteryALL_output_Wh", nBatteryALL_output_Wh);

// Debugovací výstup
if (debug) {
    node.warn(`Celkový vstup do baterie: ${nBattery_input_Wh.toFixed(2)} Wh, Celkový výstup: ${nBattery_output_Wh.toFixed(2)} Wh`);
}

msg.nBatteryALL_output_Wh = `<img src="${imageBase64Vybijeni}"> <span class="label_small">(Σ ${AutoformatValue(nBatteryALL_output_Wh)})</span>`;
msg.nBatteryALL_input_Wh = `<img src="${imageBase64Nabijeni}"> <span class="label_small">(Σ ${AutoformatValue(nBatteryALL_input_Wh)})</span>`;


msg.payload = {
    nBattery_input_Wh: nBattery_input_Wh.toFixed(2),
    nBattery_output_Wh: nBattery_output_Wh.toFixed(2),
    nBatteryALL_input_Wh: nBatteryALL_input_Wh.toFixed(2),
    nBatteryALL_output_Wh: nBatteryALL_output_Wh.toFixed(2)
};

return msg;

