// Funkce pro formátování uptime do kratšího formátu "Xd Yh Zm"
function formatUptimeShort(seconds) {
    const days = Math.floor(seconds / (3600 * 24));
    const hours = Math.floor((seconds % (3600 * 24)) / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    // Sestavíme kratší uptime string
    let uptimeString = "";
    if (days > 0) uptimeString += `${days}d `;
    if (hours > 0 || days > 0) uptimeString += `${hours}h `;
    uptimeString += `${minutes}m ${secs}s`;  // Sekundy přidáme vždy pro přesnost

    return uptimeString.trim();
}

// Načtení start time z globální proměnné
const startTime = global.get("startTime");

if (!startTime) {
    // Pokud startTime není definován, vrátí zprávu o chybě
    msg.payload = "Uptime není dostupný. Start time není nastaven.";
    return msg;
}

// Výpočet uptime
const now = Date.now();
const uptimeSeconds = Math.floor((now - startTime) / 1000); // rozdíl v sekundách

// Převod uptime na kratší formát "Xd Yh Zm"
const uptimeText = formatUptimeShort(uptimeSeconds);

msg.payload = uptimeText;
msg.fcolor = "white"; // Barva textu, můžete změnit dle potřeby

return msg;

