// color - definuje se barva primky - asi nefunguje
// smootch - true urcuje zda bude bude křivka vyhlazena pomocí pohyblivého průměru.
// api - urcuje odkud se maji brat data : global.get("sAPI_live_feed")  nebo global.get("sAPI_forecast")

const seriesConfig = [
    { name: "grid_history_to", label: "Grid History To", type: "real", sign: false, scale: 1, color: "#FF0000", smooth: false, api: "live_feed" },
    { name: "grid_history_from", label: "Grid History From", type: "real", sign: false, scale: 1, color: "#00ff00", smooth: false, api: "live_feed" },
    { name: "total_consumption", label: "Total Consumption", type: "real", sign: true, scale: 1, color: "#0000FF", smooth: false, api: "live_feed" },
    { name: "total_solar_yield", label: "Total Solar Yield", type: "real", sign: true, scale: 0.01, color: "#FFFF00", smooth: false, api: "live_feed" },
    { name: "vrm_consumption_fc", label: "VRM Consumption Forecast", type: "forecast", sign: true, scale: 0.001, color: "#FF00FF", smooth: true, api: "forecast" },
    { name: "solar_yield_forecast", label: "Solar Yield Forecast", type: "forecast", sign: true, scale: 0.001, color: "#00FFFF", smooth: true, api: "forecast" },
    { name: "Pdc", label: "Pdc", type: "real", sign: true, scale: 0.001, color: "#8A2BE2", smooth: false, api: "live_feed" },
    { name: "bs", label: "Battery State", type: "real", sign: true, scale: 0.01, color: "#A52A2A", smooth: false, api: "live_feed" },
    { name: "bv", label: "Battery Voltage", type: "real", sign: true, scale: 0.01, color: "#5F9EA0", smooth: false, api: "live_feed" },
];

// Inicializace sérií a popisků
let series = [], labels = [];
initializeSeriesAndLabels(); //Inicializace pole
fillZerosUntilNowInSeries(); // Inicializace pole podle aktualního času.

// Načtení dat do globálních proměnných, které jsou dostupné mezi flow
const nBuffer_live_feed = global.get("sAPI_live_feed") || {}; // config.api === "live_feed"
const nBuffer_forecast = global.get("sAPI_forecast") || {}; // config.api === "forecast"

// Kontrola, zda nBuffer_live_feed.records existuje
if (nBuffer_live_feed.records && nBuffer_forecast.records) {
    // Zpracování dat pro každou sérii na základě konfigurace
    seriesConfig.forEach((config, index) => {
        let records;
        if (config.api === "live_feed") {
            records = nBuffer_live_feed.records[config.name];
        } else if (config.api === "forecast") {
            records = nBuffer_forecast.records[config.name];
        }
        processSeries(records, series[index], config.sign, config.scale, config.type);

        // Aplikace vyhlazování, pokud je zapnuto
        if (config.smooth) {
            series[index] = smoothData(series[index]); // Aplikace vyhlazování
            //fWrite_LogFile("serie: ", series[index]);
        }
    });

    // Vytvoření výstupního formátu
    msg.payload = [{
        "series": seriesConfig.map(s => s.label),
        "data": series,
        "labels": labels,
        "colors": seriesConfig.map(s => s.color),
        "lines": Array(seriesConfig.length).fill(true)
    }];
} else {
    if (debug) {
        node.warn("nBuffer.records nebo jeho některé části neexistují nebo nejsou pole.");
    }
}

// Debugging
if (debug) {
    node.warn("intervalType: " + intervalType);
    node.warn("series: " + JSON.stringify(series));
    node.warn("labels: " + JSON.stringify(labels));
}

return msg;

// Funkce pro inicializaci sérií a popisků
// Funkce inicializuje potrebne pole. Pole series je inicializovano s hodnotou NULL
function initializeSeriesAndLabels() {
    const intervalCount = intervalType === "15mins" ? 96 : 24;
    series = Array(seriesConfig.length).fill().map(() => Array(intervalCount).fill(null));

    labels = Array.from({ length: intervalCount }, (_, i) => {
        const hours = String(Math.floor(i / (intervalType === "15mins" ? 4 : 1))).padStart(2, '0');
        const minutes = intervalType === "15mins" ? String((i % 4) * 15).padStart(2, '0') : "00";
        return `${hours}:${minutes}`;
    });
}

// Funkce pro doplnění hodnot 0 od 00:00 do aktuálního času v globálním poli series
// Funkce zjisti aktualni cas, a co je od daného času do minulosti je 0 a zbytek zustane NULL, tím se zajisti, že realná data nebudou staženy do 0 kdýt nejdou v té době známě hodnoty,
// Ale budou nedokončena
function fillZerosUntilNowInSeries() {
    const now = new Date();

    // Získání lokalizovaného času (CZ lokalizace)
    const oTimeCZ = now.toLocaleString(sCounty_Code, { timeZone: sTZidentifier });
    //node.warn("Localized time string: " + oTimeCZ);

    // Extrahování času z řetězce podle aktuálního formátu
    const timePart = oTimeCZ.match(/\d{1,2}:\d{2}/); // Vyhledá formát HH:MM
    if (timePart) {
        const timeParts = timePart[0].split(':');  // Rozdělení na hodiny a minuty
        const currentHours = parseInt(timeParts[0], 10);   // Hodiny
        const currentMinutes = parseInt(timeParts[1], 10); // Minuty

       // node.warn(`Current time in TZ: ${currentHours}:${currentMinutes}`);

        // Výpočet aktuálního indexu v poli podle času
        const intervalsPerHour = intervalType === "15mins" ? 4 : 1;
        const currentIntervalIndex = currentHours * intervalsPerHour + Math.floor(currentMinutes / (60 / intervalsPerHour));

        // Procházení celého pole series a doplnění hodnoty 0 od 00:00 do aktuálního času
        series.forEach((seriesData, seriesIndex) => {
            for (let i = 0; i <= currentIntervalIndex; i++) {
                if (seriesData[i] === null) {
                    seriesData[i] = 0; // Nahrazení null hodnotou 0
                }
            }

            // Výpis upraveného pole do logu po každém průchodu
            //node.warn(`series[${seriesIndex}]: ${JSON.stringify(seriesData)}`);
        });
    } else {
        //node.error("Error: Unable to extract time from localized string.");
    }
}


// Pomocná funkce pro získání indexu intervalu z timestampu
function getIntervalIndex(timestamp) {
    const date = new Date(timestamp);
    //prevest na aktualní časové pásmo.

    const hours = date.getHours();
    const minutes = date.getMinutes();
    if (intervalType === "15mins") {
        return hours * 4 + Math.floor(minutes / 15);
    } else if (intervalType === "hours") {
        return hours;
    }
    return 0; // default index if none matched
}

// Pomocná funkce pro zpracování hodnot s ohledem na znaménko
function processValue(value, isPositive, scale) {
    let processedValue = parseFloat(value) * scale;
    return isPositive ? processedValue : -processedValue;
}

function interpolateData(data) {
    const interpolatedData = [...data];  // Kopie dat pro manipulaci
    let lastKnownValue = null;
    let startIndex = null;

    // Najdi první známou hodnotu (není null, undefined nebo 0)
    for (let i = 0; i < data.length; i++) {
        if (data[i] !== null && data[i] !== undefined && data[i] !== 0) {
            lastKnownValue = parseFloat(data[i]);
            startIndex = i;
            break;
        }
    }

    // Procházej zbytek dat a interpoluj pouze hodnoty null, undefined nebo 0
    for (let i = startIndex + 1; i < data.length; i++) {
        if (data[i] !== null && data[i] !== undefined && data[i] !== 0) {
            const nextKnownValue = parseFloat(data[i]);
            const step = (nextKnownValue - lastKnownValue) / (i - startIndex);
            for (let j = startIndex + 1; j < i; j++) {
                interpolatedData[j] = (lastKnownValue + step * (j - startIndex)).toFixed(2);
            }
            lastKnownValue = nextKnownValue;
            startIndex = i;
        }
    }

    return interpolatedData;
}


// Vylepšená funkce pro vyhlazení dat
function smoothData(data, windowSize = 2) {
    const smoothedData = [];
    for (let i = 0; i < data.length; i++) {
        const start = Math.max(0, i - windowSize);
        const end = Math.min(data.length, i + windowSize + 1);
        const windowData = data.slice(start, end).filter(v => v !== null);

        const sum = windowData.reduce((acc, val) => acc + parseFloat(val), 0);
        const avg = windowData.length ? (sum / windowData.length).toFixed(2) : null;
        smoothedData.push(avg);
    }
    return smoothedData;
}

function processSeries(records, seriesData, isPositive, scale, type) {
    if (Array.isArray(records)) {
        records.forEach(item => {
            if (item && item[0] && item[1]) {
                const index = getIntervalIndex(item[0]);
                const value = parseFloat(item[1]);
                if (type === "real") {
                        if (value !== null && value !== undefined) {
                            seriesData[index] = processValue(value, isPositive, scale).toFixed(2);
                    } else {
                          seriesData[index] = null;
                    }
                } else if (type === "forecast") {
                    if (value !== null && value !== undefined) {
                        seriesData[index] = processValue(value, isPositive, scale).toFixed(2);
                    } else {
                        seriesData[index] = null;
                    }
                }
            }
        });
        // Aplikovat interpolaci jen pokud type === "forecast"
        if (type === "forecast") {
            // Po zpracování celé série aplikujeme interpolaci chybějících dat (null a undefined)
            const interpolatedSeriesData = interpolateData(seriesData);

            // Uložení interpolovaných dat zpět do series[index]
            seriesData.splice(0, seriesData.length, ...interpolatedSeriesData);
        }
    }
}

