// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

//definice globalních funkci
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');
const getStartOfDayTimestamp = global.get('getStartOfDayTimestamp');
const getEndOfDayTimestamp = global.get('getEndOfDayTimestamp');
const intervalType = global.get('intervalType');

const nVRM_site_id = fGetConfigProperty("nVRM_site_id"); //dotaz na data z globalni strukturu
const bearerToken = fGetConfigProperty("bearerToken") || 0; //dotaz na data z globalni strukturu


const tStart = getStartOfDayTimestamp();
const tStop = getEndOfDayTimestamp();
/*
curl --request GET \
  --url https://vrmapi.victronenergy.com/v2/installations/<install_id>/system-overview \
  --header 'Content-Type: application/json' \
  --header 'x-authorization: Bearer <vas_bearer_token>'
  */
// Nastavení hlaviček a těla požadavku
msg.headers = {
    "Content-Type": "application/json",
    "x-authorization": `Bearer ${bearerToken}`
};

// Nastavení parametrů dotazu
const params = {
    start: tStart, //"beginning_of_day"
    end: tStop, // "end_of_day"
    type: "forecast",
    interval: intervalType,
    //"attributeCodes[]": 'bs',
    //show_instance: true,
};


// Debugging
if (debug) {
    node.warn("tStart: " + tStart);
    node.warn("tStop: " + tStop);
}


// Nastavení payloadu
msg.payload = params;

// Nastavení URL pro vytvoření tokenu
//ttps://vrmapi.victronenergy.com/v2/installations/<install_id>/system-overview
msg.url = `https://vrmapi.victronenergy.com/v2/installations/${nVRM_site_id}/system-overview`;

return msg;

// Serialize the parameters object into a URL query string
function serializeParams(params) {
    return Object.keys(params)
        .map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key])}`)
        .join('&');
}

