// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

// První část: Vytvoření mapování pouze pro teplotní senzory
let sensors = msg.payload.records.devices;
let mapping = {};
let index = 0;

sensors.forEach(device => {
    if (device.name === "Temperature sensor") {
        mapping[index] = {
            unitID: device.instance,
            customName: device.customName || `Sensor_${device.instance}`
        };
        index++;
    }
});

// Uložení mapování do globální proměnné
global.set("sensorMapping", mapping, "memoryOnly");

if (debug) {
    node.warn("Global sensor mapping: " + JSON.stringify(global.get("sensorMapping")));
}

