// File Name        : N/A
// Title            : N/A
// Author           : http://www.prochazka.zde.cz -> hacesoft 2025
// Created          : 10-03-2025, 19:52
// Revised          : 11-03-2025, 21:00
// Version          : 1.2
// Target Platform  : Node-RED
//
//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

// Debug: Výpis celé zprávy pro diagnostiku
//node.warn("Raw message received: " + JSON.stringify(msg, null, 2));

// Ověření, kde se nachází unitID
let unitID = msg.unitId || msg.queueLengthByUnitId?.unitId || msg.modbusRequest?.unitid;

// Pokud stále není unitID, vypíšeme chybu a ukončíme zpracování
if (typeof unitID === "undefined") {
    node.warn("Chyba: unitID je undefined!");
    return null;
}

// Načtení globálního mappingu senzorů
let mapping = global.get("sensorMapping") || {};

// Hledání názvu senzoru podle unitID
let customName = "";
for (let key in mapping) {
    if (mapping[key].unitID === unitID) {
        customName = mapping[key].customName;
        break;
    }
}

// Pokud jsme nenašli odpovídající jméno senzoru, vypíšeme varování a ukončíme
if (!customName) {
    node.warn(`Variable name for unitID ${unitID} not recognized!`);
    return null;
}

// Nahrazení slov "Menic" a "Teplota" slovem "Temperature"
customName = customName.replace(/Menic|Teplota/g, "Temperature");

// Ověření a zpracování hodnoty teploty
let rawValue = msg.payload?.[0];  // První hodnota v payload poli
if (typeof rawValue === "undefined") {
    node.warn(`Chyba: Hodnota pro unitID ${unitID} je undefined!`);
    return null;
}

let value = rawValue / 100;  // Převod na °C (např. 3700 → 37.00°C)

// Oprava názvu pro globální proměnnou
let sanitizedName = customName.replace(/\s+/g, "_"); // Nahrazení mezer podtržítky

// Debugging výpis
if (debug) {
    //node.warn(`unitID: ${unitID}, value: ${value}°C, customName: ${customName} -> saved as: ${sanitizedName}`);
    node.warn(`SanitizedName: ` + sanitizedName + `, Value: ` + value);
}

// Uložení hodnoty do globální proměnné
global.set(sanitizedName, value);

// Výstup zprávy pro další zpracování
msg.payload = {
    unitID: unitID,
    value: value,
    customName: customName
};

return msg;

