    let message = {
        payload: { 
            'fc': 3,                 // Funkční kód pro čtení
            'unitid': unitID,
            'address': address,
            'quantity': quantity
        }
    };
    messages.push(message);
});

if (debug) {
    node.warn("Generated messages: " + JSON.stringify(messages));
}

// Odeslání zpráv
