// Debugging flag
const debug = false;  // Nastavte na true pro zapnutí debug zpráv

// Nastavení časových intervalů v milisekundách - pro snadnou úpravu
const INTERVAL_OKAMZITY = 0;          // Okamžitý výstup (při každém tiknutí)
const INTERVAL_MIN1 = 60000;          // 1 minuta
const INTERVAL_MIN2 = 120000;         // 2 minuty
const INTERVAL_MIN15 = 900000;        // 15 minut

// Inicializace časových značek, pokud neexistují
if (context.lastSent === undefined) {
    context.lastSent = {
        okamzity: 0,
        min1: 0,
        min2: 0,
        min15: 0
    };
    context.firstRun = true;  // Příznak prvního spuštění

    // Při prvním spuštění vrátit null pro všechny výstupy
    return [null, null, null, null];
}

const now = Date.now();
let outputs = [null, null, null, null]; // Výchozí hodnoty - nic neposílat

// Po prvním spuštění již normálně posílat zprávy
if (context.firstRun) {
    context.firstRun = false;
    // Nastavit časové značky na aktuální čas bez odeslání zpráv
    context.lastSent.okamzity = now;
    context.lastSent.min1 = now;
    context.lastSent.min2 = now;
    context.lastSent.min15 = now;
} else {
    // Kontrola pro okamžitý výstup - při každém tiknutí
    outputs[0] = { 
        payload: "Okamžitý výstup",
        timestamp: now,
        interval: "okamžitý"
    };
    context.lastSent.okamzity = now;

    // Aktualizace stavu uzlu - zobrazí čas posledního tiknutí
    const timeStr = new Date().toLocaleTimeString();
    node.status({fill:"green", shape:"dot", text: "Tik: " + timeStr});

    // Kontrola pro 1 minutový interval
    if (now - context.lastSent.min1 >= INTERVAL_MIN1) {
        outputs[1] = { 
            payload: "Posun o 1 minutu", 
            timestamp: now,
            interval: "1 minuta" 
        };
        context.lastSent.min1 = now;
    }

    // Kontrola pro 2 minutový interval
    if (now - context.lastSent.min2 >= INTERVAL_MIN2) {
        outputs[2] = { 
            payload: "Posun o 2 minuty", 
            timestamp: now,
            interval: "2 minuty" 
        };
        context.lastSent.min2 = now;
    }

    // Kontrola pro 15 minutový interval
    if (now - context.lastSent.min15 >= INTERVAL_MIN15) {
        outputs[3] = { 
            payload: "Posun o 15 minut", 
            timestamp: now,
            interval: "15 minut" 
        };
        context.lastSent.min15 = now;
    }
}

return outputs;

