// Funkce vracející začátek dne (00:00:00)
global.set('getStartOfDayTimestamp', function getStartOfDayTimestamp() {
    let now = new Date(); // Dnešní datum
    let formatter = new Intl.DateTimeFormat('en-US', {
        timeZone: sTZidentifier,
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
    });

    // Naformátujeme na datum ve správném časovém pásmu
    let parts = formatter.formatToParts(now);
    let year = parseInt(parts.find(part => part.type === 'year').value, 10);
    let month = parseInt(parts.find(part => part.type === 'month').value, 10) - 1;
    let day = parseInt(parts.find(part => part.type === 'day').value, 10);

    // Vytvoříme začátek dne
    let startOfDay = new Date(year, month, day, 0, 0, 0);

    // Vrátíme timestamp v sekundách
    return Math.floor(startOfDay.getTime() / 1000);
});

//*****************************************************************************

// Funkce vracející konec dne (23:59:59)
global.set('getEndOfDayTimestamp', function getEndOfDayTimestamp() {
    let now = new Date(); // Dnešní datum
    let formatter = new Intl.DateTimeFormat('en-US', {
        timeZone: sTZidentifier,
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
    });

    // Naformátujeme na datum ve správném časovém pásmu
    let parts = formatter.formatToParts(now);
    let year = parseInt(parts.find(part => part.type === 'year').value, 10);
    let month = parseInt(parts.find(part => part.type === 'month').value, 10) - 1;
    let day = parseInt(parts.find(part => part.type === 'day').value, 10);

    // Vytvoříme konec dne
    let endOfDay = new Date(year, month, day, 23, 59, 59);

    // Vrátíme timestamp v sekundách
    return Math.floor(endOfDay.getTime() / 1000);
});

