// Funkce pro reset pole: obnoví pracovní pole ze záložního pole
global.set('resetFilenamePaths', function () {
    const backupPaths = global.get("backup_filename_paths");

    // Zkontroluj, zda záložní pole existuje a není prázdné
    if (backupPaths && backupPaths.length > 0) {
        global.set("current_filename_paths", [...backupPaths]); // Obnova pracovní kopie
        //node.warn("Pracovní pole bylo resetováno.");
    } else {
        node.warn("Záložní pole je prázdné nebo neexistuje.");
    }
    return null;
});

//*****************************************************************************
// Funkce pro zpracování prvku (odebere první prvek z pracovního pole)
global.set('shiftFilenamePath', function () {
    let currentPaths = global.get("current_filename_paths");

    // Pokud je pracovní pole prázdné, vrátíme hlášku o prázdném poli
    if (!currentPaths || currentPaths.length === 0) {
        //node.warn("Pracovní pole je prázdné, provádím reset.");
        // Reset pole, protože je prázdné
        global.get('resetFilenamePaths')();
        currentPaths = global.get("current_filename_paths");
    }

    // Odebereme první prvek z pole
    const path = currentPaths.shift(); // Odebíráme první prvek

    // Uložíme aktualizované pracovní pole zpět do globální proměnné
    global.set("current_filename_paths", currentPaths);

    // Pokud bylo pole prázdné i po resetu
    if (!path) {
        node.warn("Pole je prázdné, žádný soubor k návratu.");
        return null;
    } else {
        // Vraťme odebranou cestu
        return path;
    }
});

