// Všechna práva vyhrazena (c) 1997 - 2025 hacesoft.
//
//*****************************************************************************
// Debugging flag
const debug = false;

// Načtení globálních funkcí
const removeSpacesAroundDots = global.get('removeSpacesAroundDots');
const sExtractTime = global.get('sExtractTime');
const sCounty_Code = global.get('sCounty_Code');
const sTZidentifier = global.get('sTZidentifier');
const fGetConfigProperty = global.get('getConfigProperty');
const fSetConfigProperty = global.get('setConfigProperty');

// Načtení konfiguračních hodnot
const isSpotAutoCtrlEnabled = !!fGetConfigProperty("isSpotAutoCtrlEnabled");
const spotTresholdPrice = fGetConfigProperty("spotTresholdPrice") || 0;

//parsovaci funkce
function parseJsonPayload(payload) {
    // Pokud je payload už objekt, vrátíme ho přímo
    if (typeof payload === 'object' && payload !== null) {
        return payload;
    }

    // Pokud payload není string, vrátíme prázdný objekt
    if (typeof payload !== 'string') {
        return {};
    }

    // Pokud payload začíná chybovou hláškou, zpracujeme ji
    if (payload.startsWith('RequestErr')) {
        node.warn("Detekována chyba požadavku: " + payload);
        return { error: payload };
    }

    // Pokusíme se zparsovat payload jako JSON
    try {
        return JSON.parse(payload);
    } catch (error) {
        node.error("Chyba při převodu JSON: " + error.message);
        return { error: error.message };
    }
}

// Společná funkce pro určení barvy podle hodnoty a prahu
function getPriceColorClass(price) {
    if (price < 0) {
        return 'red';
    } else if (price >= 0 && price <= spotTresholdPrice) {
        return 'yellow';
    } else {
        return 'green';
    }
}

function getLocalizedDate() {
    const currentTime = new Date();
    const sDate = currentTime.toLocaleString(sCounty_Code, { timeZone: sTZidentifier });
    return sExtractTime(removeSpacesAroundDots(sDate));
}

function createPriceChart(hourlyPrices, showCurrentTime = false) {
    if (!hourlyPrices || hourlyPrices.length === 0) {
        return { payload: [], minmax: "N/A | N/A" };
    }

    const line1 = [];
    const line2 = [];
    const currentTimeLine = [];
    const oDate_CZ = getLocalizedDate();
    const currentTime = new Date();

    const minPrice = Math.min(...hourlyPrices);
    const maxPrice = Math.max(...hourlyPrices);
    const currPrice = hourlyPrices[oDate_CZ.getHours()];

    // Vytvoření dat pro graf
    for (let i = 0; i < 24; i++) {
        const hourDate = new Date(oDate_CZ);
        hourDate.setUTCHours(i, 0, 0);
        line1.push({ "x": hourDate, "y": hourlyPrices[i] });
        line2.push({ "x": hourDate, "y": spotTresholdPrice });
    }

    // Přidání posledního bodu pro dokončení čáry grafu
    const lastHourDate = new Date(oDate_CZ);
    lastHourDate.setUTCHours(23, 59, 59);
    line1.push({ "x": lastHourDate, "y": hourlyPrices[23] });
    line2.push({ "x": lastHourDate, "y": spotTresholdPrice });

    // Příprava dat pro výstup
    let series = ["Kč/kWh", "Spot limit"];
    let data = [line1, line2];

    // Přidání markeru pro aktuální čas, pokud je vyžadováno
    if (showCurrentTime && currPrice !== undefined) {
        // Nastavení časového posunu
        const timezoneOffsetHours = -currentTime.getTimezoneOffset() / 60;
        const adjustedCurrentTime = new Date(currentTime.getTime() + timezoneOffsetHours * 60 * 60 * 1000);

        currentTimeLine.push({ "x": adjustedCurrentTime, "y": currPrice });
        currentTimeLine.push({ "x": adjustedCurrentTime, "y": ((minPrice < spotTresholdPrice) ? minPrice : spotTresholdPrice) - 0.1 });
        currentTimeLine.push({ "x": adjustedCurrentTime, "y": ((maxPrice > spotTresholdPrice) ? maxPrice : spotTresholdPrice) + 0.1 });

        series.push("Aktuálně");
        data.push(currentTimeLine);
    }

    return {
    payload: [{ "series": series, "data": data }],
    minmax: `
        <span class="label_size15">
            <span class="${getPriceColorClass(minPrice)}">
                ${minPrice.toFixed(2)}
            </span>
            | 
            <span class="${getPriceColorClass(maxPrice)}">
                ${maxPrice.toFixed(2)}
            </span>
            Kč/kWh
        </span>
    `
    };
}

// Hlavní zpracování
// Převod JSON řetězce na objekt
const originalPayload = msg.payload;
msg.payload = parseJsonPayload(msg.payload);

if (!msg.payload) {
    return [null, null, null, null];
}

// Kontrola dostupnosti potřebných globálních funkcí
const globalFunctionsAvailable = removeSpacesAroundDots && sExtractTime && sCounty_Code && sTZidentifier;
if (!globalFunctionsAvailable) {
    node.error("Globální funkce nebyly nalezeny.");
    return [null, null, null, null];
}

// Vytvoření objektů pro jednotlivé výstupy
const msg1 = { topic: msg.topic };
const msg2 = { topic: msg.topic };
const msg3 = { topic: msg.topic };
const msg4 = { topic: msg.topic };

// Zpracování vstupních dat
const currentPrice = msg.payload.currentPrice;
const todayHourlyPrices = msg.payload.todayHourlyPrices || [];
const tomorrowHourlyPrices = msg.payload.tomorrowHourlyPrices || [];

// První výstup - graf pro dnešní ceny
if ("todayHourlyPrices" in msg.payload) {
    const chartData = createPriceChart(todayHourlyPrices, true);
    msg1.payload = chartData.payload;
    msg1.minmax = chartData.minmax;
} else {
    msg1.payload = [];
}

// Druhý výstup - graf pro zítřejší ceny
if ("tomorrowHourlyPrices" in msg.payload) {
    const chartData = createPriceChart(tomorrowHourlyPrices, false);
    msg2.payload = chartData.payload;
    msg2.minmax = chartData.minmax;
} else {
    msg2.payload = [];
}

// Uložení aktuální ceny do globální proměnné, pokud je k dispozici
if (currentPrice !== undefined) {
    global.set("currentPrice", currentPrice);

    if (todayHourlyPrices.length > 0) {
        global.set("todayHourlyPrices", todayHourlyPrices);
    }
}

// Třetí výstup - zpracování spotových cen
if (currentPrice !== undefined) {
    msg3.payload = `<span class="label_size15">
            <span class="${getPriceColorClass(currentPrice)}">
                ${currentPrice.toFixed(2)}
            </span>Kč/kWh
        </span>`;
} else {
    msg3.payload = null;
}


// Čtvrtý výstup - ovládání spotových cen
if (currentPrice !== undefined) {
    let nSpot = 0;
    let swOnOff = 0;

    if (isSpotAutoCtrlEnabled && currentPrice >= spotTresholdPrice) {
        nSpot = 1;
        swOnOff = 1;
    }

    msg4.payload = {
        "nSpot": nSpot,
        "currentPrice": currentPrice,
        "hCurrentPrice": 1,
        "swOnOff": swOnOff,
        "isSpotAutoCtrlEnabled": isSpotAutoCtrlEnabled,
        "spotTresholdPrice": spotTresholdPrice
    };

    if (debug) {
        node.warn("nSpot: " + nSpot);
    }
} else {
    msg4.payload = null;
}

// Vrátíme všechny čtyři zprávy, každou pro jeden výstup
return [msg1, msg2, msg3, msg4];
