// Generov√°n√≠ n√°hodn√©ho alfanumerick√©ho ≈ôetƒõzce
function generateRandomString(length) {
    let chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
}

// Sanitizace ≈ôetƒõzce - odstranƒõn√≠ diakritiky a nebezpeƒçn√Ωch znak≈Ø
function sanitizeString(input) {
    if (typeof input !== 'string') {
        return 'NCC-1701-D'; // Vr√°tit U.S.S. Enterprise NCC-1701-D, pokud vstup nen√≠ ≈ôetƒõzec
    }
    // Odstranƒõn√≠ diakritiky
    input = input.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    // Povolen√© znaky: p√≠smena a ƒç√≠slice
    return input.replace(/[^a-zA-Z0-9]/g, '');
}

